import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // Import React Router's useNavigate hook

const PanelSelection = () => {
    const [selectedStates, setSelectedStates] = useState({
        frontRight: false,
        frontLeft: false,
        frontComplete: false,
        backRight: false,
        backLeft: false,
        backComplete: false,
        sleeveRight: false,
        sleeveLeft: false,
        bothSleeves: false,
        tape: false,
        kharcha1: false,
        kharcha2: false,
        kharcha3: false
    });

    const navigate = useNavigate();

    const handleCheckboxChange = (event) => {
        const { name, checked } = event.target;
        setSelectedStates((prevState) => ({
            ...prevState,
            [name]: checked
        }));
    };

    const handleNext = () => {
        // Pass the selected states to the AddKnittingDetailsForm component through navigation
        navigate('/add-knitting-details', { state: selectedStates });
    };

    return (
        <div>
            {Object.keys(selectedStates).map((state) => (
                <div key={state}>
                    <label>
                        <input
                            type="checkbox"
                            name={state}
                            checked={selectedStates[state]}
                            onChange={handleCheckboxChange}
                        />
                        {state}
                    </label>
                </div>
            ))}

            {/* Next Button */}
            <button onClick={handleNext}>
                Next
            </button>
        </div>
    );
};

export default PanelSelection;
