import React, { useState } from 'react';
import { searchMachineByMachineNo } from '../API/MachineApi';
import UpdateMachine from './UpdateMachine'; // Assume you have UpdateMachine component

const SearchMachine = () => {
  const [machineNo, setMachineNo] = useState('');
  const [machine, setMachine] = useState(null);
  const [error, setError] = useState('');
  const [showUpdate, setShowUpdate] = useState(false);

  const handleSearch = async () => {
    try {
      const result = await searchMachineByMachineNo(machineNo);
      setMachine(result.data);
      setError('');
    } catch (err) {
      setError('Machine not found');
      setMachine(null);
    }
  };

  const handleUpdate = () => {
    setShowUpdate(true);
  };

  return (
    <div>
      <h1>Search Machine</h1>
      <input
        type="text"
        placeholder="Enter MachineNo"
        value={machineNo}
        onChange={(e) => setMachineNo(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      
      {machine && !showUpdate && (
        <div>
          <h2>Machine Details</h2>
          <table>
        <thead>
          <tr>
            <th>MachineNo</th>
            <th>ModelNo</th>
            <th>MachineType</th>
            <th>Category</th>
            <th>Count Period</th>
            <th>Machine System</th>
            <th>MachineStatus</th>
            <th>Remarks</th>
          </tr>
        </thead>
        <tbody>
          {machine.length > 0 ? (
            machine.map((machine) => (
              <tr key={machine.MachineNo}>
                <td>{machine.MachineNo}</td>
                <td>{machine.ModelNo}</td>
                <td>{machine.MachineType}</td>
                <td>{machine.Category}</td>
                <th>{machine.CountPeriod}</th>
                <th>{machine.MachineSystem}</th>
                <td>{machine.MachineStatus}</td>
                <th>{machine.Remarks}</th>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="6">No machines found</td>
            </tr>
          )}
        </tbody>
      </table>
          <button onClick={handleUpdate}>Update</button>
        </div>
      )}

      {showUpdate && <UpdateMachine machine={machine} setShowUpdate={setShowUpdate} />}
    </div>
  );
};

export default SearchMachine;
