import React, { useState, useEffect } from "react";
import { getYarnUsageByArticleNo } from "../API/YarnUsageApi";

const AddOrderDetails = ({ ArticleNo }) => {
  const [yarnUsageData, setYarnUsageData] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchYarnUsage = async () => {
      try {
        const response = await getYarnUsageByArticleNo(ArticleNo);
        if (response.success && response.data && response.data.length > 0) {
          setYarnUsageData(response.data);
        } else {
          setError("No data found for this ArticleNo.");
        }
      } catch (err) {
        setError("Failed to fetch yarn usage data.");
      } finally {
        setLoading(false);
      }
    };

    fetchYarnUsage();
  }, [ArticleNo]);

  if (loading) {
    return <div>Loading...</div>;
  }

  // Show error if any
  if (error) {
    return <div>{error}</div>;
  }

  // Function to get non-null sizes dynamically
  const getNonNullSizes = (data) => {
    const sizes = ["Freesize", "XS", "S", "M", "L", "XL", "2XL", "3XL", "4XL", "5XL"];
    return sizes.filter((size) => data[size] !== null && data[size] !== undefined);
  };

  // Display the yarn usage data in a table format
  return (
    <div>
      <h2>Yarn Usage Details for Article No: {ArticleNo}</h2>
      {yarnUsageData.length > 0 ? (
        <table border="1" cellPadding="10" style={{ width: "100%", marginTop: "20px" }}>
          <thead>
            <tr>
              <th>Matching Name</th>
              {/* Dynamically render headers based on non-null sizes */}
              {["Freesize", "XS", "S", "M", "L", "XL", "2XL", "3XL", "4XL", "5XL"].map(
                (size) =>
                  yarnUsageData.some((data) => data[size] !== null && data[size] !== undefined) && (
                    <th key={size}>{size}</th>
                  )
              )}
            </tr>
          </thead>
          <tbody>
            {yarnUsageData.map((data, dataIndex) => {
              const nonNullSizes = getNonNullSizes(data);
              return (
                <tr key={dataIndex}>
                  <td>{data.MatchingName}</td>
                  {/* Render values for non-null sizes */}
                  {["Freesize", "XS", "S", "M", "L", "XL", "2XL", "3XL", "4XL", "5XL"].map(
                    (size) =>
                      nonNullSizes.includes(size) ? (
                        <td key={size}>{data[size]}</td>
                      ) : (
                        <td key={size}></td>
                      )
                  )}
                </tr>
              );
            })}
          </tbody>
        </table>
      ) : (
        <div>No yarn usage data found.</div>
      )}
    </div>
  );
};

export default AddOrderDetails;
