// src/components/AddEmployeeForm.js

import React, { useState } from 'react';
import { addEmployee } from '../API/EmployeeApi';

const AddEmployeeDetails = () => {
    // State to hold form input values
    const [employeeData, setEmployeeData] = useState({
        EmpId: '',
        EmpName: '',
        Address: '',
        EmailId: '',
        ContactNo: '',
        AltContactNo: '',
        DOB: '',
        Age: '',
        JoiningDate: '',
        Designation: '',
        Anniversary: '',
    });

    // State for loading and error messages
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [successMessage, setSuccessMessage] = useState(null);

    // Handle input changes
    const handleChange = (e) => {
        const { name, value } = e.target;
        setEmployeeData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError(null); // Reset error message

        try {
            // Call the addEmployee function to submit data
            const response = await addEmployee(employeeData);

            // Handle successful response
            setSuccessMessage('Employee added successfully!');
            setEmployeeData({}); // Reset form data
        } catch (err) {
            // Handle error response
            setError('Failed to add employee. Please try again.');
        } finally {
            setLoading(false); // Stop loading
        }
    };

    return (
        <div>
            <h1>Add New Employee</h1>
            <form onSubmit={handleSubmit}>
                {/* Input fields for employee data */}
                <input
                    type="text"
                    name="EmpId"
                    value={employeeData.EmpId}
                    onChange={handleChange}
                    placeholder="Employee ID"
                    required
                />
                <input
                    type="text"
                    name="EmpName"
                    value={employeeData.EmpName}
                    onChange={handleChange}
                    placeholder="Employee Name"
                    required
                />
                <input
                    type="text"
                    name="Address"
                    value={employeeData.Address}
                    onChange={handleChange}
                    placeholder="Address"
                />
                <input
                    type="email"
                    name="EmailId"
                    value={employeeData.EmailId}
                    onChange={handleChange}
                    placeholder="Email ID"
                    required
                />
                <input
                    type="number"
                    name="ContactNo"
                    value={employeeData.ContactNo}
                    onChange={handleChange}
                    placeholder="Contact No"
                    required
                />
                <input
                    type="number"
                    name="AltContactNo"
                    value={employeeData.AltContactNo}
                    onChange={handleChange}
                    placeholder="Alternate Contact No"
                />
                <input
                    type="date"
                    name="DOB"
                    value={employeeData.DOB}
                    onChange={handleChange}
                    placeholder="Date of Birth"
                    required
                />
                <input
                    type="number"
                    name="Age"
                    value={employeeData.Age}
                    onChange={handleChange}
                    placeholder="Age"
                />
                <input
                    type="date"
                    name="JoiningDate"
                    value={employeeData.JoiningDate}
                    onChange={handleChange}
                    placeholder="Joining Date"
                    required
                />
                <input
                    type="text"
                    name="Designation"
                    value={employeeData.Designation}
                    onChange={handleChange}
                    placeholder="Designation"
                    required
                />
                <input
                    type="date"
                    name="Anniversary"
                    value={employeeData.Anniversary}
                    onChange={handleChange}
                    placeholder="Anniversary"
                />

                <button type="submit" disabled={loading}>
                    {loading ? 'Adding...' : 'Add Employee'}
                </button>
            </form>

            {error && <p style={{ color: 'red' }}>{error}</p>}
            {successMessage && <p style={{ color: 'green' }}>{successMessage}</p>}
        </div>
    );
};

export default AddEmployeeDetails;
