import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { getColorMatchingByRSN } from "../API/ColorApi";

const AddArticle = () => {
  const location = useLocation();
  const navigate = useNavigate();

  // RSN fetched from location state
  const RSN = location.state ? location.state.RSN : null;

  // State for form data
  const [articleNumber, setArticleNumber] = useState("");
  const [sizes, setSizes] = useState({
    freesize: false,
    xs: false,
    s: false,
    m: false,
    l: false,
    xl: false,
    xxl: false,
    "3xl": false,
    "4xl": false,
    "5xl": false,
  });
  const [selectedColors, setSelectedColors] = useState([]);
  const [colorOptions, setColorOptions] = useState([]);

  // Fetch matching colors based on RSN
  useEffect(() => {
    if (RSN) {
      getColorMatchingByRSN(RSN)
        .then((data) => {
          // Assuming the response contains a list of colors with a 'MatchingName'
          setColorOptions(data);
        })
        .catch((error) => console.error("Error fetching colors:", error));
    }
  }, [RSN]);

  // Handle form submission
  const handleSubmit = (event) => {
    event.preventDefault();

    // Prepare form data
    const formData = {
      RSN,
      articleNumber,
      sizes,
      selectedColors,
    };

    console.log("Form data:", formData);

    // Example of navigating after submission (you can replace this with your actual submit logic)
    navigate("/success"); // Assuming you have a success route
  };

  // Handle checkbox change for sizes
  const handleSizeChange = (e) => {
    const { name, checked } = e.target;
    setSizes((prevSizes) => ({
      ...prevSizes,
      [name]: checked,
    }));
  };

  // Handle checkbox change for selected colors
  const handleColorChange = (e) => {
    const { name, checked } = e.target;
    setSelectedColors((prevColors) =>
      checked
        ? [...prevColors, name]
        : prevColors.filter((color) => color !== name)
    );
  };

  return (
    <div>
      <h1>Add Article</h1>
      <form onSubmit={handleSubmit}>
        {/* RSN Field (read-only) */}
        <div>
          <label>RSN:</label>
          <input type="text" value={RSN} readOnly />
        </div>

        {/* Article Number Field */}
        <div>
          <label>Article Number:</label>
          <input
            type="text"
            value={articleNumber}
            onChange={(e) => setArticleNumber(e.target.value)}
            required
          />
        </div>

        {/* Sizes Checkboxes */}
        <div>
          <label>Sizes:</label>
          <div>
            {Object.keys(sizes).map((size) => (
              <div key={size}>
                <input
                  type="checkbox"
                  name={size}
                  checked={sizes[size]}
                  onChange={handleSizeChange}
                />
                <label>{size.toUpperCase()}</label>
              </div>
            ))}
          </div>
        </div>

        {/* Colors Checkboxes */}
        <div>
          <label>Selected Colors:</label>
          <div>
            {colorOptions.map((color) => (
              <div key={color.MatchingName}>
                <input
                  type="checkbox"
                  name={color.MatchingName}
                  onChange={handleColorChange}
                />
                <label>{color.MatchingName}</label>
              </div>
            ))}
          </div>
        </div>

        {/* Submit Button */}
        <div>
          <button type="submit">Submit</button>
        </div>
      </form>
    </div>
  );
};

export default AddArticle;
