import React, { useState } from 'react';
import { addCuttingData, getCuttingByRSN } from '../API/CuttingApi';

const AddCutting = () => {
  const [cuttingData, setCuttingData] = useState(null);
  const [RSN, setRSN] = useState();
  const [newCuttingData, setNewCuttingData] = useState({
    RSN: '',
    EmpID: '',
    Cost: '',
    UserId: ''
  });

  const handleAddCutting = async () => {
    try {
      const response = await addCuttingData(newCuttingData);
      alert(response.message);  // Display success message
    } catch (error) {
      alert('Failed to add cutting data');
    }
  };

  const handleFetchCuttingByRSN = async (RSN) => {
    try {
      const response = await getCuttingByRSN(RSN);
      setCuttingData(response.data);
    } catch (error) {
      alert('Failed to fetch cutting data');
    }
  };

  return (
    <div>
      {/* Add Cutting Form */}
      <input
        type="text"
        value={newCuttingData.RSN}
        onChange={(e) => setNewCuttingData({ ...newCuttingData, RSN: e.target.value })}
        placeholder="RSN"
      />
      <input
        type="text"
        value={newCuttingData.EmpID}
        onChange={(e) => setNewCuttingData({ ...newCuttingData, EmpID: e.target.value })}
        placeholder="EmpID"
      />
      <input
        type="number"
        value={newCuttingData.Cost}
        onChange={(e) => setNewCuttingData({ ...newCuttingData, Cost: e.target.value })}
        placeholder="Cost"
      />
      <input
        type="text"
        value={newCuttingData.UserId}
        onChange={(e) => setNewCuttingData({ ...newCuttingData, UserId: e.target.value })}
        placeholder="UserId"
      />
      <button onClick={handleAddCutting}>Add Cutting Data</button>

      {/* Fetch Cutting by RSN */}
      <input
        type="text"
        placeholder="Enter RSN to fetch"
        onChange={(e) => setRSN(e.target.value )}
      />

      <button onClick={handleFetchCuttingByRSN}>Submit</button>

      {cuttingData && (
        <div>
          <h3>Fetched Cutting Data</h3>
          <pre>{JSON.stringify(cuttingData, null, 2)}</pre>
        </div>
      )}
    </div>
  );
};

export default AddCutting;
