// src/components/UpdateKnittingRecord.js
import React, { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom'; // to get the RSN from URL params
import { getKnittingDetailsByRSN, updateKnittingDetails } from '../api'; // Import the API functions

const UpdateKnittingRecord = () => {
  const { RSN } = useParams(); // Get RSN from the URL (e.g., /update/:RSN)
  const history = useHistory(); // Used to navigate after successful update

  // State variables to store form data
  const [knittingDetails, setKnittingDetails] = useState({
    RSN: '',
    Size: '',
    FrontRight: '',
    FrontLeft: '',
    FrontComplete: '',
    BackRight: '',
    BackLeft: '',
    BackComplete: '',
    SleeveRight: '',
    SleeveLeft: '',
    BothSleeves: '',
    Tape: '',
    Collar: '',
    Kharcha1: '',
    Kharcha2: '',
    Kharcha3: '',
  });

  const [error, setError] = useState(null);
  const [message, setMessage] = useState(null);

  // Fetch the knitting details when the component mounts
  useEffect(() => {
    const fetchKnittingDetails = async () => {
      try {
        const response = await getKnittingDetailsByRSN(RSN);
        setKnittingDetails(response.data);
      } catch (error) {
        setError('Error fetching knitting details.');
        console.error('Error:', error);
      }
    };

    fetchKnittingDetails();
  }, [RSN]);

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await updateKnittingDetails(RSN, knittingDetails);

      // Handle success
      if (response.success) {
        setMessage('Knitting details updated successfully.');
        setTimeout(() => {
          history.push('/'); // Redirect after successful update (change to your desired route)
        }, 2000);
      } else {
        setError('Failed to update knitting details.');
      }
    } catch (err) {
      setError('An error occurred while updating the details.');
      console.error('Error:', err);
    }
  };

  // Handle input change
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setKnittingDetails((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  return (
    <div>
      <h2>Update Knitting Details for RSN: {RSN}</h2>
      {message && <p style={{ color: 'green' }}>{message}</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <form onSubmit={handleSubmit}>
        <div>
          <label>RSN:</label>
          <input
            type="text"
            name="RSN"
            value={knittingDetails.RSN}
            disabled
          />
        </div>
        <div>
          <label>Size:</label>
          <input
            type="text"
            name="Size"
            value={knittingDetails.Size}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>FrontRight (JSON):</label>
          <textarea
            name="FrontRight"
            value={knittingDetails.FrontRight}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>FrontLeft (JSON):</label>
          <textarea
            name="FrontLeft"
            value={knittingDetails.FrontLeft}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>FrontComplete (JSON):</label>
          <textarea
            name="FrontComplete"
            value={knittingDetails.FrontComplete}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>BackRight (JSON):</label>
          <textarea
            name="BackRight"
            value={knittingDetails.BackRight}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>BackLeft (JSON):</label>
          <textarea
            name="BackLeft"
            value={knittingDetails.BackLeft}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>BackComplete (JSON):</label>
          <textarea
            name="BackComplete"
            value={knittingDetails.BackComplete}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>SleeveRight (JSON):</label>
          <textarea
            name="SleeveRight"
            value={knittingDetails.SleeveRight}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>SleeveLeft (JSON):</label>
          <textarea
            name="SleeveLeft"
            value={knittingDetails.SleeveLeft}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>BothSleeves (JSON):</label>
          <textarea
            name="BothSleeves"
            value={knittingDetails.BothSleeves}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Tape (JSON):</label>
          <textarea
            name="Tape"
            value={knittingDetails.Tape}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Collar (JSON):</label>
          <textarea
            name="Collar"
            value={knittingDetails.Collar}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Kharcha1 (JSON):</label>
          <textarea
            name="Kharcha1"
            value={knittingDetails.Kharcha1}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Kharcha2 (JSON):</label>
          <textarea
            name="Kharcha2"
            value={knittingDetails.Kharcha2}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Kharcha3 (JSON):</label>
          <textarea
            name="Kharcha3"
            value={knittingDetails.Kharcha3}
            onChange={handleInputChange}
          />
        </div>

        <button type="submit">Update Knitting Details</button>
      </form>
    </div>
  );
};

export default UpdateKnittingRecord;
