import React, { useState, useEffect } from "react";
import { getColorDetailByColorId } from "../API/ColorDetailApi";

// Utility function to check for non-null, non-zero values
const isValidValue = (value) => {
  return value !== null && value !== "" && value !== 0;
};

const ShowColorDetail = ({ ColorId }) => {
  const [colorDetail, setColorDetail] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    if (ColorId) {
      const fetchData = async () => {
        setLoading(true);
        try {
          const response = await getColorDetailByColorId(ColorId);
          setColorDetail(response.data); // Store color detail data
        } catch (err) {
          setError("Failed to fetch color details.");
        } finally {
          setLoading(false);
        }
      };

      fetchData();
    }
  }, [ColorId]); // Trigger effect whenever ColorId changes

  if (loading) return <p>Loading...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  // Handle the case when colorDetail is an array with multiple entries
  if (!colorDetail || colorDetail.length === 0) {
    return <p>No color details available for this Color ID.</p>;
  }

  console.log(colorDetail);
  // Access the first item in the array
  const details = colorDetail[0];

  // Define columns based on the Color keys
  const colorKeys = [
    "BaseColor", "Color1", "Color2", "Color3", "Color4", "Color5", 
    "Color6", "Color7", "Color8", "Color9", "Color10", "Color11", 
    "Color12", "Color13", "Color14"
  ];

  console.log(details);
  return (
    <div>
      <h1>Color Detail for Color ID: {ColorId}</h1>
      {colorDetail ? (
        <div>
          <h2>Details</h2>
          <table border="1" style={{ width: "100%", marginTop: "20px" }}>
            <thead>
              <tr>
                <th>Color Field</th>
                <th>Name</th>
                <th>Weight</th>
              </tr>
            </thead>
            <tbody>
              {colorKeys.map((colorKey, index) => {
                const colorData = details[colorKey];

                // Only render rows where both Name and Weight are valid (non-null, non-zero)
                if (colorData && isValidValue(colorData.Name) && isValidValue(colorData.Weight)) {
                  return (
                    <tr key={index}>
                      <td>{colorKey}</td>
                      <td>{colorData.Name}</td>
                      <td>{colorData.Weight}</td>
                    </tr>
                  );
                } else {
                  return null;
                }
              })}
            </tbody>
          </table>
        </div>
      ) : (
        <p>No color details available for this Color ID.</p>
      )}
    </div>
  );
};

export default ShowColorDetail;
