import React, { useState, useEffect } from "react";
import { searchOrderYarn } from "../API/OrderYarnApi"; // Import the API function

const ShowOrderYarn = () => {
  const orderNo = '67';
  const [orderYarnData, setOrderYarnData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch order yarn details when the component mounts or when OrderNo changes
  useEffect(() => {
    if (!orderNo) return; // If OrderNo is not provided, don't make the request

    const fetchOrderYarnData = async () => {
      try {
        setLoading(true);
        setError(null); // Clear previous error

        const response = await searchOrderYarn(orderNo); // Call the API with OrderNo
        setOrderYarnData(response.data); // Set the response data to state
      } catch (err) {
        setError("Error fetching order yarn data");
        console.error("Error fetching order yarn data:", err);
      } finally {
        setLoading(false); // Set loading to false after request is completed
      }
    };

    fetchOrderYarnData();
  }, [orderNo]); // Dependency array ensures this runs only when OrderNo changes

  // Show loading message
  if (loading) {
    return <p>Loading order yarn data...</p>;
  }

  // Show error message if an error occurred
  if (error) {
    return <p>{error}</p>;
  }

  // Show order yarn details if available
  return (
    <div>
      <h2>Search Order Yarn Data</h2>

      {/* Show order yarn data if available */}
      {orderYarnData ? (
        <div>
          <h3>Order Yarn Data for OrderNo: {orderNo}</h3>
          <table>
            <thead>
              <tr>
                <th>Field Name</th>
                <th>Value</th>
              </tr>
            </thead>
            <tbody>
              {Object.keys(orderYarnData).map((key) => (
                orderYarnData[key] !== null && (
                  <tr key={key}>
                    <td>{key}</td>
                    <td>{orderYarnData[key]}</td>
                  </tr>
                )
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <p>No data found for this OrderNo.</p>
      )}
    </div>
  );
};

export default ShowOrderYarn;
