import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom"; // useLocation to get RSN from state
import { updateKnittingDetails } from "../API/SampleApi"; // Assuming this API function exists

const UpdateKnittingDetails = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { RSN } = location.state || {};

  const [knittingData, setKnittingData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  useEffect(() => {
    if (RSN) {
      const fetchData = async () => {
        setLoading(true);
        try {
          const response = await getKnittingDetailsByRSN(RSN);
          setKnittingData(response.data);
        } catch (err) {
          setError("Failed to fetch knitting details.");
        } finally {
          setLoading(false);
        }
      };
      fetchData();
    }
  }, [RSN]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!knittingData) {
      setError("No data to update.");
      return;
    }

    setLoading(true);
    try {
      const response = await updateKnittingDetails(RSN, knittingData);
      setSuccessMessage(response.message);
      setError("");
    } catch (err) {
      setError("Failed to update knitting details.");
      setSuccessMessage("");
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div>
      <h1>Update Knitting Details</h1>
      {error && <p style={{ color: "red" }}>{error}</p>}
      {successMessage && <p style={{ color: "green" }}>{successMessage}</p>}

      <form onSubmit={handleSubmit}>
        {knittingData && (
          <div>
            <div>
              <label>Field 1:</label>
              <input
                type="text"
                value={knittingData.Field1 || ""}
                onChange={(e) => setKnittingData({ ...knittingData, Field1: e.target.value })}
              />
            </div>
            <button type="submit" disabled={loading}>
              {loading ? "Updating..." : "Update Record"}
            </button>
          </div>
        )}
      </form>
    </div>
  );
};

export default UpdateKnittingDetails;
