import React, { useState, useEffect } from "react";
import { getYarnUsageByArticleNo } from "../API/YarnUsageApi";

const AddOrderDetails = ({ ArticleNo }) => {
  const [yarnUsageData, setYarnUsageData] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [quantities, setQuantities] = useState({});

  useEffect(() => {
    const fetchYarnUsage = async () => {
      try {
        const response = await getYarnUsageByArticleNo(ArticleNo);
        if (response.success && response.data && response.data.length > 0) {
          setYarnUsageData(response.data);
        } else {
          setError("No data found for this ArticleNo.");
        }
      } catch (err) {
        setError("Failed to fetch yarn usage data.");
      } finally {
        setLoading(false);
      }
    };

    fetchYarnUsage();
  }, [ArticleNo]);

  if (loading) {
    return <div>Loading...</div>;
  }

  // Show error if any
  if (error) {
    return <div>{error}</div>;
  }

  // List of all possible sizes (to ensure we check across all data)
  const sizes = ["Freesize", "XS", "S", "M", "L", "XL", "2XL", "3XL", "4XL", "5XL"];

  // Function to get the sizes that have non-null values across all MatchingNames
  const getAllNonNullSizes = (data) => {
    // Find all sizes that are non-null in any row
    const nonNullSizes = sizes.filter((size) => data.some(row => row[size] !== null));
    return nonNullSizes;
  };

  // Get all non-null sizes across all yarn usage data
  const nonNullSizes = getAllNonNullSizes(yarnUsageData);

    // Function to handle quantity input changes
    const handleQuantityChange = (matchingName, size, value) => {
        setQuantities((prevQuantities) => ({
          ...prevQuantities,
          [matchingName]: {
            ...prevQuantities[matchingName],
            [size]: value,
          },
        }));
      };

  // Display the yarn usage data in a table format
  return (
    <div>
      <h2>Order Quantity Details for Article No: {ArticleNo} with RSN : {yarnUsageData[0].RSN}</h2>
      {yarnUsageData.length > 0 ? (
        <table border="1" cellPadding="10" style={{ width: "100%", marginTop: "20px" }}>
          <thead>
            <tr>
              <th>Matching Name</th>
              {nonNullSizes.map((size) => (
                <th key={size}>{size}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {yarnUsageData.map((data, dataIndex) => (
              <tr key={dataIndex}>
                <td>{data.MatchingName}</td>
                {nonNullSizes.map((size) => (
                  <td key={size}><input
                  type="number"
                  value={quantities[data.MatchingName]?.[size] || ""}
                  onChange={(e) =>
                    handleQuantityChange(data.MatchingName, size, e.target.value)
                  }
                  min="0"
                  style={{ width: "50px" }}
                /></td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <div>No yarn usage data found.</div>
      )}
    </div>
  );
};

export default AddOrderDetails;
