import React, { useState, useEffect } from "react";
import axios from "axios";

const SampleDetails = () => {
  const [sampleDetails, setSampleDetails] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/SampleDetails")
      .then((response) => {
        setSampleDetails(response.data);
        setLoading(false);
      })
      .catch((error) => {
        setError(error.message);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <p>Loading Sample Details...</p>;
  }

  if (error) {
    return <p>Error fetching sample details: {error}</p>;
  }
  
  useEffect(() => {
    // Assuming you fetch sampleDetails from an API or some source
    fetchSampleDetails().then(data => setSampleDetails(data));
  }, []);

  return (
    <div>
      <h1>Sample Details</h1>
      <table>
        <thead>
          <tr>
            <th>RSN</th>
            <th>ArticleName</th>
            <th>DesignFileNo</th>
            <th>SeriesArticleFileNo</th>
            <th>ArticleType</th>
            <th>Gender</th>
            <th>MachineSpeed</th>
            <th>Designer</th>
            <th>Grapher</th>
            <th>SampleMaster</th>
            <th>SampleStatus</th>
          </tr>
        </thead>
        <tbody>
          {sampleDetails.map((sample) => (
            <tr key={sample.RSN}>
              <td>{sample.RSN}</td>
              <td>{sample.ArticleName}</td>
              <td>{sample.DesignFileNo}</td>
              <td>{sample.SeriesArticleFileNo}</td>
              <td>{sample.ArticleType}</td>
              <td>{sample.Gender}</td>
              <td>{sample.MachineSpeed}</td>
              <td>{sample.Designer}</td>
              <td>{sample.Grapher}</td>
              <td>{sample.SampleMaster}</td>
              <td>{sample.SampleStatus}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default SampleDetails;
