import React, { useState } from 'react';
import { searchMachineByMachineNo } from '../API/MachineApi';
import UpdateMachine from './UpdateMachine'; // Assume you have UpdateMachine component

const SearchMachine = () => {
  const [machineNo, setMachineNo] = useState('');
  const [machine, setMachine] = useState(null);
  const [error, setError] = useState('');
  const [showUpdate, setShowUpdate] = useState(false);

  const handleSearch = async () => {
    try {
      const result = await searchMachineByMachineNo(machineNo);
      setMachine(result.data);
      setError('');
    } catch (err) {
      setError('Machine not found');
      setMachine(null);
    }
  };

  const handleDelete = async () => {
    try {
      const result = await deleteMachineByMachineNo(machine.MachineNo);
      if (result.success) {
        setMachine(null);
        setError('Machine deleted successfully');
      }
    } catch (err) {
      setError('Error deleting machine');
    }
  };

  const handleUpdate = () => {
    setShowUpdate(true);
  };

  return (
    <div>
      <h1>Search Machine</h1>
      <input
        type="text"
        placeholder="Enter MachineNo"
        value={machineNo}
        onChange={(e) => setMachineNo(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      
      {machine && !showUpdate && (
        <div>
          <h2>Machine Details</h2>
          <p>MachineNo: {machine.MachineNo}</p>
          <p>ModelNo: {machine.ModelNo}</p>
          <p>MachineType: {machine.MachineType}</p>
          <p>Category: {machine.Category}</p>
          <p>MachineStatus: {machine.MachineStatus}</p>
          <button onClick={handleUpdate}>Update</button>
          <button onClick={handleDelete}>Delete</button>
        </div>
      )}

      {showUpdate && <UpdateMachine machine={machine} setShowUpdate={setShowUpdate} />}
    </div>
  );
};

export default SearchMachine;
