import React, { useState } from 'react';
import { addColorDetail } from '../API/ColorDetailApi';

const AddColorDetails = () => {
  // Define initial state for the form with default values
  const initialState = {
    ColorId: '',
    Size: '',
    BaseColor: '',
    colors: Array(14).fill({ name: null, weight: 0 }), // Default name is null, weight is 0
    totalWeight: 0, // Total weight field
  };

  const [formData, setFormData] = useState([initialState]);

  // Handle change for input fields (ColorId, Size, BaseColor, and each Color Name/Weight)
  const handleInputChange = (e, index, field) => {
    const { name, value } = e.target;
    const updatedFormData = [...formData];

    if (field === 'colors') {
      updatedFormData[index].colors[name] = value;
    } else {
      updatedFormData[index][name] = value;
    }

    // Recalculate total weight whenever a weight field is changed
    if (field === 'colors' && name.includes('weight')) {
      updatedFormData[index].totalWeight = updatedFormData[index].colors.reduce((sum, color) => {
        return sum + (parseFloat(color.weight) || 0); // Sum up all color weights, defaulting to 0 if invalid
      }, 0);
    }

    setFormData(updatedFormData);
  };

  // Handle adding a new row
  const handleAddRow = () => {
    setFormData([...formData, initialState]);
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Prepare the data in the expected format
    const colorData = formData.map(row => {
      const colors = row.colors.reduce((acc, color, index) => {
        acc[`Color${index + 1}`] = { name: color.name, weight: color.weight };
        return acc;
      }, {});

      return {
        ColorId: row.ColorId,
        Size: row.Size,
        BaseColor: row.BaseColor,
        totalWeight: row.totalWeight, // Add totalWeight to the object
        ...colors, // Add Color1, Color2, ... Color14 to the object
      };
    });

    try {
      // Send the data to the backend via the addColorDetail API call from ColordetailsApi.js
      const response = await Promise.all(colorData.map(data => addColorDetail(data)));
      console.log(response); // Log the successful response
    } catch (error) {
      console.error("Error submitting form:", error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <table border="1" style={{ width: '100%', tableLayout: 'auto' }}>
        <thead>
          <tr>
            <th rowSpan="2" style={{ width: '8%' }}>ColorId</th>
            <th rowSpan="2" style={{ width: '8%' }}>Size</th>
            <th rowSpan="2" style={{ width: '8%' }}>Base Color</th>
            {/* Color 1 to Color 14 with sub-columns for Name and Weight */}
            {[...Array(14)].map((_, index) => (
              <th colSpan="2" key={index} style={{ width: '10%' }}>
                Color {index + 1}
              </th>
            ))}
            <th rowSpan="2" style={{ width: '8%' }}>Total Weight</th>
          </tr>
          <tr>
            {/* Sub-columns for each color */}
            {[...Array(14)].map((_, index) => (
              <React.Fragment key={index}>
                <th>Name</th>
                <th>Weight</th>
              </React.Fragment>
            ))}
          </tr>
        </thead>
        <tbody>
          {formData.map((row, rowIndex) => (
            <tr key={rowIndex}>
              <td>
                <input
                  type="text"
                  name="ColorId"
                  value={row.ColorId}
                  onChange={(e) => handleInputChange(e, rowIndex, 'ColorId')}
                />
              </td>
              <td>
                <input
                  type="text"
                  name="Size"
                  value={row.Size}
                  onChange={(e) => handleInputChange(e, rowIndex, 'Size')}
                />
              </td>
              <td>
                <input
                  type="text"
                  name="BaseColor"
                  value={row.BaseColor}
                  onChange={(e) => handleInputChange(e, rowIndex, 'BaseColor')}
                />
              </td>

              {/* Loop through Color1 to Color14 */}
              {row.colors.map((color, colorIndex) => (
                <React.Fragment key={colorIndex}>
                  <td>
                    <input
                      type="text"
                      name={colorIndex}
                      value={color.name || ''}
                      placeholder={`Color ${colorIndex + 1} Name`}
                      onChange={(e) => handleInputChange(e, rowIndex, 'colors')}
                    />
                  </td>
                  <td>
                    <input
                      type="number"
                      name={colorIndex}
                      value={color.weight || 0}
                      placeholder={`Color ${colorIndex + 1} Weight`}
                      onChange={(e) => handleInputChange(e, rowIndex, 'colors')}
                    />
                  </td>
                </React.Fragment>
              ))}

              {/* Total Weight */}
              <td>
                <input
                  type="number"
                  name="totalWeight"
                  value={row.totalWeight}
                  readOnly
                  style={{ backgroundColor: '#f0f0f0' }} // Make it read-only
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <button type="button" onClick={handleAddRow}>Add Row</button>
      <button type="submit">Submit</button>
    </form>
  );
};

export default AddColorDetails;
