import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom"; // useLocation to get passed state
import { updateKnittingDetails } from "../API/SampleApi"; // Assuming you have an API to update data

const UpdateKnittingRecord = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { RSN, knittingData } = location.state || {};

  // Debugging log for knittingData
  console.log("Knitting Data:", knittingData);

  const [updatedData, setUpdatedData] = useState(knittingData || {});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  useEffect(() => {
    if (!knittingData) {
      setError("No knitting data available.");
    }
  }, [knittingData]);

  // Handle input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!updatedData) {
      setError("No data to update.");
      return;
    }

    setLoading(true);
    try {
      const response = await updateKnittingDetails(RSN, updatedData);
      console.log("Response:", response);  // Log response for debugging
      setSuccessMessage(response.message);
      setError("");
    } catch (err) {
      console.error("Error:", err);  // Log error for debugging
      setError("Failed to update knitting details.");
      setSuccessMessage("");
    } finally {
      setLoading(false);
    }

    navigate(`/show-sample/${RSN}`, { state: { RSN } });
  };

  if (loading) return <p>Loading...</p>;

  // Render form fields
  const renderFormFields = (obj) => {
    return Object.entries(obj).map(([key, value]) => {
      if (typeof value === "object" && value !== null) {
        return (
          <div key={key}>
            <label>{key}:</label>
            <div>{renderFormFields(value)}</div> {/* Recursively render nested fields */}
          </div>
        );
      } else {
        return (
          <div key={key}>
            <label>{key}:</label>
            <input
              type="text"
              name={key}
              value={value || ""}
              onChange={handleInputChange}
            />
          </div>
        );
      }
    });
  };

  return (
    <div>
      <h1>Update Knitting Details for RSN: {RSN}</h1>
      {error && <p style={{ color: "red" }}>{error}</p>}
      {successMessage && <p style={{ color: "green" }}>{successMessage}</p>}

      <form onSubmit={handleSubmit}>
        {renderFormFields(updatedData)}
        <button type="submit" disabled={loading}>
          {loading ? "Updating..." : "Update Record"}
        </button>
      </form>
    </div>
  );
};

export default UpdateKnittingRecord;
