// src/components/UpdateKnittingDetails.js
import React, { useState } from 'react';
import { updateKnittingDetails } from '../api'; // Import the API function

const UpdateKnittingDetails = () => {
  // Define state for the form fields
  const [RSN, setRSN] = useState('');
  const [Size, setSize] = useState('');
  const [FrontRight, setFrontRight] = useState('');
  const [FrontLeft, setFrontLeft] = useState('');
  const [FrontComplete, setFrontComplete] = useState('');
  const [BackRight, setBackRight] = useState('');
  const [BackLeft, setBackLeft] = useState('');
  const [BackComplete, setBackComplete] = useState('');
  const [SleeveRight, setSleeveRight] = useState('');
  const [SleeveLeft, setSleeveLeft] = useState('');
  const [BothSleeves, setBothSleeves] = useState('');
  const [Tape, setTape] = useState('');
  const [Collar, setCollar] = useState('');
  const [Kharcha1, setKharcha1] = useState('');
  const [Kharcha2, setKharcha2] = useState('');
  const [Kharcha3, setKharcha3] = useState('');
  const [error, setError] = useState(null);
  const [message, setMessage] = useState(null);

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Construct the form data (assume JSON strings are valid for simplicity)
    const knittingDetails = {
      RSN,
      Size,
      FrontRight: JSON.parse(FrontRight),
      FrontLeft: JSON.parse(FrontLeft),
      FrontComplete: JSON.parse(FrontComplete),
      BackRight: JSON.parse(BackRight),
      BackLeft: JSON.parse(BackLeft),
      BackComplete: JSON.parse(BackComplete),
      SleeveRight: JSON.parse(SleeveRight),
      SleeveLeft: JSON.parse(SleeveLeft),
      BothSleeves: JSON.parse(BothSleeves),
      Tape: JSON.parse(Tape),
      Collar: JSON.parse(Collar),
      Kharcha1: JSON.parse(Kharcha1),
      Kharcha2: JSON.parse(Kharcha2),
      Kharcha3: JSON.parse(Kharcha3),
    };

    try {
      // Call the API to update the knitting details
      const response = await updateKnittingDetails(knittingDetails);

      // Handle success
      if (response.success) {
        setMessage('Knitting details updated successfully.');
      } else {
        setError('Failed to update knitting details.');
      }
    } catch (err) {
      setError('An error occurred while updating the details.');
      console.error('Error:', err);
    }
  };

  return (
    <div>
      <h2>Update Knitting Details</h2>
      {message && <p style={{ color: 'green' }}>{message}</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}
      
      <form onSubmit={handleSubmit}>
        <div>
          <label>RSN:</label>
          <input
            type="text"
            value={RSN}
            onChange={(e) => setRSN(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Size:</label>
          <input
            type="text"
            value={Size}
            onChange={(e) => setSize(e.target.value)}
            required
          />
        </div>
        <div>
          <label>FrontRight (JSON):</label>
          <textarea
            value={FrontRight}
            onChange={(e) => setFrontRight(e.target.value)}
          />
        </div>
        <div>
          <label>FrontLeft (JSON):</label>
          <textarea
            value={FrontLeft}
            onChange={(e) => setFrontLeft(e.target.value)}
          />
        </div>
        <div>
          <label>FrontComplete (JSON):</label>
          <textarea
            value={FrontComplete}
            onChange={(e) => setFrontComplete(e.target.value)}
          />
        </div>
        <div>
          <label>BackRight (JSON):</label>
          <textarea
            value={BackRight}
            onChange={(e) => setBackRight(e.target.value)}
          />
        </div>
        <div>
          <label>BackLeft (JSON):</label>
          <textarea
            value={BackLeft}
            onChange={(e) => setBackLeft(e.target.value)}
          />
        </div>
        <div>
          <label>BackComplete (JSON):</label>
          <textarea
            value={BackComplete}
            onChange={(e) => setBackComplete(e.target.value)}
          />
        </div>
        <div>
          <label>SleeveRight (JSON):</label>
          <textarea
            value={SleeveRight}
            onChange={(e) => setSleeveRight(e.target.value)}
          />
        </div>
        <div>
          <label>SleeveLeft (JSON):</label>
          <textarea
            value={SleeveLeft}
            onChange={(e) => setSleeveLeft(e.target.value)}
          />
        </div>
        <div>
          <label>BothSleeves (JSON):</label>
          <textarea
            value={BothSleeves}
            onChange={(e) => setBothSleeves(e.target.value)}
          />
        </div>
        <div>
          <label>Tape (JSON):</label>
          <textarea
            value={Tape}
            onChange={(e) => setTape(e.target.value)}
          />
        </div>
        <div>
          <label>Collar (JSON):</label>
          <textarea
            value={Collar}
            onChange={(e) => setCollar(e.target.value)}
          />
        </div>
        <div>
          <label>Kharcha1 (JSON):</label>
          <textarea
            value={Kharcha1}
            onChange={(e) => setKharcha1(e.target.value)}
          />
        </div>
        <div>
          <label>Kharcha2 (JSON):</label>
          <textarea
            value={Kharcha2}
            onChange={(e) => setKharcha2(e.target.value)}
          />
        </div>
        <div>
          <label>Kharcha3 (JSON):</label>
          <textarea
            value={Kharcha3}
            onChange={(e) => setKharcha3(e.target.value)}
          />
        </div>

        <button type="submit">Update Knitting Details</button>
      </form>
    </div>
  );
};

export default UpdateKnittingDetails;
