import React, { useState, useEffect } from 'react';
import { getImageByRSN } from '../API/ImageApi';

const ShowImage = ({ RSN }) => {
  const [imageData, setImageData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchImage = async () => {
      try {
        setLoading(true);
        setError(null);
        const data = await getImageByRSN(RSN);
        setImageData(data.ImageData); // Assuming the data contains ImageData (base64)
        console.log(imageData);
      } catch (err) {
        setError('Error fetching image');
      } finally {
        setLoading(false);
      }
    };

    fetchImage();
  }, [RSN]);

  if (loading) {
    return <p>Loading image...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  return (
    <div>
      {imageData ? (
        <img src={`data:image/jpeg;base64,${imageData}`} alt="Sample" style={{ width: '100%', maxWidth: '600px' }} />
      ) : (
        <p>No image found for this RSN.</p>
      )}
    </div>
  );
};

export default ShowImage;
