import React, { useState, useEffect } from 'react';
import { getCommentsByRSN, addComment } from '../API/CommentApi';
import { useSelector } from "react-redux";

const Comments = ({ RSN }) => {
  // State hooks for comments and new comment input
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const [isCommentBoxVisible, setIsCommentBoxVisible] = useState(false); // For controlling comment box visibility
  
  // Get userId from the Redux store
  const userId = useSelector((state) => state.user.userId); // Assuming the userId is stored in state.user.userId

  // Fetch comments on component mount or when RSN changes
  const fetchComments = async () => {
    try {
      const result = await getCommentsByRSN(RSN);
      setComments(result.data);
    } catch (error) {
      console.error('Failed to fetch comments:', error.message);
    }
  };

  useEffect(() => {
    fetchComments();
  }, [RSN]);

  // Handle submitting a new comment
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!newComment.trim()) {
      alert("Comment cannot be empty.");
      return;
    }

    try {
      // Add the new comment using the addComment API function
      const result = await addComment(RSN, userId, newComment);
      setNewComment(''); // Clear the new comment input
      setIsCommentBoxVisible(false); // Hide the comment box after submission
      alert(result.message || 'Comment added successfully');

      // Re-fetch the comments after successful submission
      fetchComments();
    } catch (error) {
      alert(error.message || 'Failed to add comment');
    }
  };

  // Handle showing and hiding the comment textbox
  const handleAddCommentClick = () => {
    setIsCommentBoxVisible(true); // Show the textbox when the button is clicked
  };

  return (
    <div>
      <h2>Comments for RSN: {RSN}</h2>
      <div>
        <h3>Comment Section</h3>
        <ul>
          {comments.length === 0 ? (
            <li>No comments available</li>
          ) : (
            comments.map((comment, index) => (
              <li key={index}>{comment.Comments}</li>
            ))
          )}
        </ul>
      </div>

      <div>
        {!isCommentBoxVisible ? (
          <button onClick={handleAddCommentClick}>Add Comment</button>
        ) : (
          <div>
            <h3>Add a Comment</h3>
            <form onSubmit={handleSubmit}>
              <textarea
                value={newComment}
                onChange={(e) => setNewComment(e.target.value)}
                placeholder="Enter your comment here"
                required
              />
              <div>
                <button type="submit">Submit Comment</button>
                <button type="button" onClick={() => setIsCommentBoxVisible(false)}>Cancel</button>
              </div>
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

export default Comments;
