import React, { useState } from "react";
import { addColorDetail } from "../API/ColorDetailApi"; // Assuming this API exists

const AddColorDetails = ({ colorPanelMapping, matchingName, RSN, selectedStates }) => {
  const [formData, setFormData] = useState([]);
  const initialState = {
    ColorId: "",
    BaseColor: { name: null, weight: 0 },
    colors: Array(14).fill({ name: null, weight: 0 }),
    totalWeight: 0,
  };

  React.useEffect(() => {
    const panels = Object.values(selectedStates).filter((value) => value === true);
    setFormData(panels.map((panel) => ({
      ...initialState,
      ColorId: colorPanelMapping[matchingName], // Get the ColorId based on the matchingName
    })));
  }, [selectedStates, colorPanelMapping, matchingName]);

  const handleInputChange = (e, index, field) => {
    const { name, value } = e.target;
    const updatedFormData = [...formData];

    if (field === "colors") {
      updatedFormData[index].colors[name] = value;
    } else if (field === "BaseColor") {
      updatedFormData[index].BaseColor[name] = value;
    } else {
      updatedFormData[index][name] = value;
    }

    updatedFormData[index].totalWeight = updatedFormData[index].colors.reduce(
      (sum, color) => sum + (parseFloat(color.weight) || 0), 0
    );

    setFormData(updatedFormData);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const colorData = formData.map((row) => ({
      ColorId: row.ColorId,
      BaseColor: row.BaseColor,
      totalWeight: row.totalWeight,
      colors: row.colors.reduce((acc, color, index) => {
        acc[`Color${index + 1}`] = { name: color.name, weight: color.weight };
        return acc;
      }, {}),
    }));

    try {
      await Promise.all(colorData.map((data) => addColorDetail(data)));
      alert("Color details submitted successfully");
    } catch (error) {
      console.error("Error submitting form:", error);
    }
  };

  return (
    <div>
      <h3>{matchingName}</h3>
      <form onSubmit={handleSubmit}>
        <table>
          <thead>
            <tr>
              <th>ColorId</th>
              <th>Base Color</th>
              {[...Array(14)].map((_, index) => (
                <th key={index}>Color {index + 1}</th>
              ))}
              <th>Total Weight</th>
            </tr>
          </thead>
          <tbody>
            {formData.map((row, rowIndex) => (
              <tr key={rowIndex}>
                <td>
                  <input
                    type="text"
                    value={row.ColorId}
                    readOnly
                    style={{ backgroundColor: "#f0f0f0" }}
                  />
                </td>
                <td>
                  <input
                    type="text"
                    value={row.BaseColor.name || ""}
                    placeholder="Base Color Name"
                    onChange={(e) => handleInputChange(e, rowIndex, "BaseColor")}
                  />
                  <input
                    type="number"
                    value={row.BaseColor.weight || 0}
                    placeholder="Base Color Weight"
                    onChange={(e) => handleInputChange(e, rowIndex, "BaseColor")}
                  />
                </td>
                {row.colors.map((color, colorIndex) => (
                  <td key={colorIndex}>
                    <input
                      type="text"
                      value={color.name || ""}
                      placeholder={`Color ${colorIndex + 1} Name`}
                      onChange={(e) => handleInputChange(e, rowIndex, "colors")}
                    />
                    <input
                      type="number"
                      value={color.weight || 0}
                      placeholder={`Color ${colorIndex + 1} Weight`}
                      onChange={(e) => handleInputChange(e, rowIndex, "colors")}
                    />
                  </td>
                ))}
                <td>
                  <input
                    type="number"
                    value={row.totalWeight}
                    readOnly
                    style={{ backgroundColor: "#f0f0f0" }}
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default AddColorDetails;
