import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import { addColorMatching } from "../API/ColorApi";

const ColorMatching = ({ onSubmit }) => {
  const location = useLocation();
  const { RSN, selectedStates, size } = location.state || {};

  const [numColors, setNumColors] = useState(0);
  const [colorMatches, setColorMatches] = useState([]);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState("");

  // Function to generate ColorId
  const generateColorId = () => {
    const firstAndLastLetters = buttonNames
      .map((name) => {
        if (name) {
          return name[0].toUpperCase() + name[name.length - 1].toUpperCase();
        }
        return "";
      })
      .join(""); // Join first and last letters of each button name

    const selectedUppercase = Object.entries(selectedStates)
      .filter(([key, value]) => value === true) // Filter only fields where value is true
      .map(([key, value]) => key) // Get the keys (fields) that are true
      .filter((key) => /^[A-Z0-9]+$/.test(key)) // Keep only uppercase letters or numerals
      .join(""); // Join them into a single string

    return `${RSN}${firstAndLastLetters}${selectedUppercase}`;
  };

  // Handle the number of color matches input change
  const handleNumColorsChange = (e) => {
    const num = parseInt(e.target.value, 10);
    setNumColors(num);
    setColorMatches(Array(num).fill("")); // Reset color matches array based on the number
  };

  // Handle the input change for each color match
  const handleColorMatchChange = (index, e) => {
    const newColorMatches = [...colorMatches];
    newColorMatches[index] = e.target.value;
    setColorMatches(newColorMatches);
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate that each color match field is filled
    if (colorMatches.some((match) => match === "")) {
      setError("All color match fields must be filled.");
      return;
    }

    setLoading(true);
    setError(""); // Clear any previous errors
    setSuccess(""); // Clear any previous success message

    const colorData = {
      colorMatches,
    };

    try {
      const response = await addColorMatching(colorData);
      console.log("Color matching added:", response);
      setSuccess("Color matching added successfully!");
      // Reset the form after successful submission
      setNumColors(0);
      setColorMatches([]);
    } catch (error) {
      setError("An error occurred while adding color matching.");
      console.error("Error:", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Add Color Matching</h2>
      
      <form onSubmit={handleSubmit}>
        {/* Step 1: Input for the number of color matches */}
        <div>
          <label>Number of color matches: </label>
          <input
            type="number"
            value={numColors}
            onChange={handleNumColorsChange}
            min="1"
            required
          />
        </div>

        {/* Step 2: Render input fields for color matches */}
        {Array.from({ length: numColors }).map((_, index) => (
          <div key={index}>
            <label>Color Match {index + 1}:</label>
            <input
              type="text"
              value={colorMatches[index] || ""}
              onChange={(e) => handleColorMatchChange(index, e)}
              required
            />
          </div>
        ))}

        {/* Submit Button */}
        <button type="submit" disabled={loading}>
          {loading ? "Submitting..." : "Add Color Matching"}
        </button>
      </form>

      {/* Display Error or Success Messages */}
      {error && <p style={{ color: "red" }}>{error}</p>}
      {success && <p style={{ color: "green" }}>{success}</p>}
    </div>
  );
};

export default ColorMatching;
