import React, { useState } from "react";
import { updateImage } from "../API/ImageApi";
import { useSelector } from "react-redux";

const UpdateImage = ({ RSN }) => {
  const userId = useSelector((state) => state.user.userId);
  const [imageFile, setImageFile] = useState(null);
  const [imageName, setImageName] = useState(""); // Optional: You can set a default name for the image
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(null);

  // Handle file selection
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImageFile(file);
    }
  };

  // Handle form submission to update image
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!imageFile) {
      setError("Please select an image to upload.");
      return;
    }

    setLoading(true);
    setError(null);
    setSuccess(null);

    try {
      const response = await updateImage(imageFile, imageName, RSN, userId);
      setSuccess(response.message);  // Handle success message from API
      setImageFile(null);  // Reset the file input
      setImageName("");  // Reset the image name
    } catch (err) {
      setError("Error uploading the image. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Update Image</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Image Name:</label>
          <input
            type="text"
            value={imageName}
            onChange={(e) => setImageName(e.target.value)}
            placeholder="Enter image name"
          />
        </div>

        <div>
          <label>Select Image to Upload:</label>
          <input
            type="file"
            accept="image/*"
            onChange={handleFileChange}
          />
        </div>

        {error && <div style={{ color: "red" }}>{error}</div>}
        {success && <div style={{ color: "green" }}>{success}</div>}

        <div>
          <button type="submit" disabled={loading}>
            {loading ? "Uploading..." : "Update Image"}
          </button>
        </div>
      </form>
    </div>
  );
};

export default UpdateImage;
