import React, { useState, useEffect } from "react";
import { getKnittingDetailsByRSN } from "../API/SampleApi";

const ShowKnittingDetails = ({ RSN }) => {
  const [knittingData, setKnittingData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    if (RSN) {
      const fetchData = async () => {
        setLoading(true);
        try {
          const response = await getKnittingDetailsByRSN(RSN);
          setKnittingData(response.data); // Assuming the response contains knitting details
        } catch (err) {
          setError("Failed to fetch knitting details.");
        } finally {
          setLoading(false);
        }
      };

      fetchData();
    }
  }, [RSN]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  // Function to check for non-null, non-zero, and non-empty values
  const isNonZero = (value) => {
    // If it's an object (e.g., Weight and Time), check each property
    if (typeof value === "object" && value !== null) {
      return (
        (value.Weight && value.Weight !== 0) || // Weight is non-zero
        (value.Time && value.Time !== 0) // Time is non-zero
      );
    }
    return value !== 0 && value !== "" && value !== null && value !== undefined;
  };

  return (
    <div>
      <h1>Knitting Details</h1>
      {knittingData ? (
        <table border="1" style={{ width: "100%", marginTop: "20px" }}>
          <thead>
            <tr>
              <th>Field</th>
              <th>Weight</th>
              <th>Time</th>
            </tr>
          </thead>
          <tbody>
            {Object.entries(knittingData).map(([key, value]) => {
              // For each row, we check if the value is an object with `Weight` and `Time`
              if (typeof value === "object" && value !== null) {
                return (
                  <tr key={key}>
                    <td>{key}</td>
                    {/* If the value for Weight or Time is null, 0, or empty, display N/A */}
                    <td>{value.Weight || "N/A"}</td> {/* Render Weight */}
                    <td>{value.Time || "N/A"}</td> {/* Render Time */}
                  </tr>
                );
              }

              // For non-object values (if the value is just a number, string, etc.)
              return (
                <tr key={key}>
                  <td>{key}</td>
                  <td colSpan="2">{value || "N/A"}</td> {/* Render non-object values */}
                </tr>
              );
            })}
          </tbody>
        </table>
      ) : (
        <p>No knitting details available</p>
      )}
    </div>
  );
};

export default ShowKnittingDetails;
