import React, { useState, useEffect } from "react";
import { addColorDetail } from "../API/ColorDetailApi";
import { getColorId } from "../API/ColorApi";

const AddColorDetails = ({ matchingName, RSN, size, selectedStates }) => {
  // Initial form data state with default structure
  const initialFormData = {
    ColorId: "",
    Size: size,
    BaseColor: { Name: "", Weight: "" },
    Color1: { Name: "", Weight: "" },
    Color2: { Name: "", Weight: "" },
    Color3: { Name: "", Weight: "" },
    Color4: { Name: "", Weight: "" },
    Color5: { Name: "", Weight: "" },
    Color6: { Name: "", Weight: "" },
    Color7: { Name: "", Weight: "" },
    Color8: { Name: "", Weight: "" },
    Color9: { Name: "", Weight: "" },
    Color10: { Name: "", Weight: "" },
    Color11: { Name: "", Weight: "" },
    Color12: { Name: "", Weight: "" },
    Color13: { Name: "", Weight: "" },
    Color14: { Name: "", Weight: "" },
  };

  const [formData, setFormData] = useState([initialFormData]);  // Initialize with an array for multiple panels
  const [colorIds, setColorIds] = useState({});

  // Extract selected panels from the selectedStates object
  const selectedPanels = Object.entries(selectedStates)
    .filter(([_, value]) => value === true)
    .map(([key]) => key);

  useEffect(() => {
    // Initialize formData only when selectedPanels is available
    if (selectedPanels.length > 0) {
      setFormData(
        selectedPanels.map((panelName) => ({
          ...initialFormData,
          ColorId: panelName, // Add panel name as ColorId
        }))
      );
    }
  }, [selectedStates]); // Re-run when selectedStates changes

  // Fetch ColorId for each selected panel
  const fetchColorId = async () => {
    try {
      const newColorIds = {};

      // Loop through selected panels and fetch ColorId
      for (const Panel of selectedPanels) {
        const data = await getColorId(RSN, matchingName, Panel);
        newColorIds[Panel] = data.ColorId; // Use only Panel as the key
      }

      // Store all ColorIds in the state
      setColorIds(newColorIds);
    } catch (error) {
      console.error("Error fetching ColorId:", error);
    }
  };

  // Handle input change for BaseColor and other colors
  const handleInputChange = (e, index, field, colorIndex = null) => {
    const { name, value } = e.target;
    const updatedFormData = [...formData];

    if (field === "colors" && colorIndex !== null) {
      // Update the specific color field (either name or weight)
      updatedFormData[index].colors[colorIndex][name] = value;
    } else if (field === "BaseColor") {
      updatedFormData[index].BaseColor[name] = value;
    } else {
      updatedFormData[index][name] = value;
    }

    setFormData(updatedFormData);
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Prepare the data in the expected format
    const colorData = formData.map((row) => {
      const colors = row.colors.reduce((acc, color, index) => {
        acc[`Color${index + 1}`] = { name: color.name, weight: color.weight };
        return acc;
      }, {});

      return {
        ColorId: row.ColorId,
        BaseColor: row.BaseColor,
        ...colors,
      };
    });

    try {
      // Send the data to the backend
      const response = await Promise.all(
        colorData.map((data) => addColorDetail(data))
      );
      console.log(response); // Log the successful response
    } catch (error) {
      console.error("Error submitting form:", error);
    }
  };

  return (
    <div>
      <button onClick={fetchColorId}>Fetch ColorIds</button>
      <h3>{matchingName}</h3>
      <form onSubmit={handleSubmit}>
        <table border="1" style={{ width: "100%", tableLayout: "auto" }}>
          <thead>
            <tr>
              <th style={{ width: "4%" }}>Panel</th>
              <th style={{ width: "4%" }}>Base Color</th>
              {[...Array(14)].map((_, index) => (
                <th key={index} style={{ width: "6%" }}>
                  Color {index + 1}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {formData.map((row, rowIndex) => (
              <tr key={rowIndex}>
                {/* Panel Column - display the selected panel name */}
                <td>{row.ColorId}</td>

                {/* Base Color */}
                <td>
                  <input
                    type="text"
                    name="Name"
                    value={row.BaseColor.Name || ""}
                    placeholder="Base Color Name"
                    onChange={(e) => handleInputChange(e, rowIndex, "BaseColor")}
                  />
                  <input
                    type="number"
                    name="Weight"
                    value={row.BaseColor.Weight || ""}
                    placeholder="Base Color Weight"
                    onChange={(e) => handleInputChange(e, rowIndex, "BaseColor")}
                  />
                </td>

                {/* Color columns */}
                {[...Array(14)].map((_, colorIndex) => (
                  <React.Fragment key={colorIndex}>
                    <td>
                      <input
                        type="text"
                        name="Name"
                        value={row[`Color${colorIndex + 1}`].Name || ""}
                        placeholder={`Color ${colorIndex + 1} Name`}
                        onChange={(e) => handleInputChange(e, rowIndex, "colors", colorIndex)}
                      />
                      <input
                        type="number"
                        name="Weight"
                        value={row[`Color${colorIndex + 1}`].Weight || ""}
                        placeholder={`Color ${colorIndex + 1} Weight`}
                        onChange={(e) => handleInputChange(e, rowIndex, "colors", colorIndex)}
                      />
                    </td>
                  </React.Fragment>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default AddColorDetails;
