import React, { useState, useEffect } from "react";
import { updateEmployee } from "../API/EmployeeApi"; // Import the update function

const formatDate = (date) => {
  const jsDate = new Date(date);
  const options = {
    year: "numeric",
    month: "long",
    day: "numeric",
  };
  return jsDate.toLocaleDateString("en-US", options);
};

const UpdateEmployee = ({ employee, onSave, onCancel }) => {
  const [updatedEmployee, setUpdatedEmployee] = useState({ ...employee });

  useEffect(() => {
    setUpdatedEmployee({ ...employee }); // Update state when employee prop changes
  }, [employee]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUpdatedEmployee((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSave = async () => {
    try {
      const result = await updateEmployee(updatedEmployee);
      if (result.success) {
        onSave(result.data); // Pass the updated data back to the parent component
      } else {
        alert("Error updating employee");
      }
    } catch (err) {
      alert("Error updating employee");
    }
  };

  return (
    <div>
      <h1>Update Employee</h1>
      <form>
        <div>
          <label>Employee ID</label>
          <input
            type="text"
            name="EmpId"
            value={updatedEmployee.EmpId}
            readOnly
          />
        </div>
        <div>
          <label>Name</label>
          <input
            type="text"
            name="EmpName"
            value={updatedEmployee.EmpName}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Address</label>
          <input
            type="text"
            name="Address"
            value={updatedEmployee.Address}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Email ID</label>
          <input
            type="email"
            name="EmailId"
            value={updatedEmployee.EmailId}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Contact No</label>
          <input
            type="text"
            name="ContactNo"
            value={updatedEmployee.ContactNo}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Alternate Contact No</label>
          <input
            type="text"
            name="AltContactNo"
            value={updatedEmployee.AltContactNo}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Date of Birth</label>
          <input
            type="date"
            name="DOB"
            value={formatDate(updatedEmployee.DOB)}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Age</label>
          <input
            type="number"
            name="Age"
            value={updatedEmployee.Age}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Joining Date</label>
          <input
            type="date"
            name="JoiningDate"
            value={updatedEmployee.JoiningDate}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Designation</label>
          <input
            type="text"
            name="Designation"
            value={updatedEmployee.Designation}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Anniversary Date</label>
          <input
            type="date"
            name="Anniversary"
            value={updatedEmployee.Anniversary}
            onChange={handleChange}
          />
        </div>
        <div>
          <button type="button" onClick={handleSave}>
            Save
          </button>
          <button type="button" onClick={onCancel}>
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default UpdateEmployee;
