import React, { useEffect, useState } from 'react';
import { getAllMachines } from '../API/MachineApi';
import { Link } from 'react-router-dom';

const ShowAllMachines = () => {
  const [machines, setMachines] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchMachines = async () => {
      try {
        const result = await getAllMachines();
        setMachines(result.data);
      } catch (err) {
        setError('Error fetching machines');
      }
    };
    fetchMachines();
  }, []);

  return (
    <div>
      <h1>All Machines</h1>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <table>
        <thead>
          <tr>
            <th>MachineNo</th>
            <th>ModelNo</th>
            <th>MachineType</th>
            <th>Category</th>
            <th>MachineStatus</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {machines.length > 0 ? (
            machines.map((machine) => (
              <tr key={machine.MachineNo}>
                <td>{machine.MachineNo}</td>
                <td>{machine.ModelNo}</td>
                <td>{machine.MachineType}</td>
                <td>{machine.Category}</td>
                <td>{machine.MachineStatus}</td>
                <td>
                  <Link to={`/update-machine/${machine.MachineNo}`}>Update</Link>
                  <button onClick={() => handleDelete(machine.MachineNo)}>Delete</button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="6">No machines found</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default ShowAllMachines;
