import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

// Function to upload an image
export const uploadImage = async (imageData, imageName, RSN, userId) => {
  const formData = new FormData();
  formData.append('Image', imageData); // Append image data
  formData.append('ImageName', imageName);
  formData.append('RSN', RSN);
  formData.append('UserId', userId);

  try {
    console.log("Starting image upload...");
    const response = await axios.post('http://localhost:5000/api/upload-image', formData, {
      headers: { 'Content-Type': 'multipart/form-data' },
      timeout: 10000,  // Increased timeout to 10 seconds
    });

    if (response && response.data) {
      console.log("Server response:", response.data);
      return response.data; // Assuming the server returns a message
    }
  } catch (error) {
    console.error("Error during image upload:", error);
    if (error.response) {
      console.error("Response error:", error.response.data);
    } else if (error.request) {
      console.error("No response from server:", error.request);
    } else {
      console.error("Request setup error:", error.message);
    }
    throw error;
  }
};

// Function to fetch image by RSN
export const getImageByRSN = async (RSN) => {
  try {
    // Ensure RSN is provided and valid
    if (!RSN) {
      throw new Error('RSN is required to fetch image');
    }

    const response = await axios.get(`${API_URL}/image/${RSN}`);
    
    // Ensure response is valid and return the image data
    if (response && response.data) {
      return response.data; // This should return ImageData (base64 string or file path)
    } else {
      throw new Error('Invalid response from server');
    }
  } catch (error) {
    // Log and handle different error cases
    console.error("Error fetching image:", error);

    if (error.response) {
      // If the server responded with an error
      console.error('Response error:', error.response.data);
      console.error('Status code:', error.response.status);
    } else if (error.request) {
      // If no response was received
      console.error('No response from server:', error.request);
    } else {
      // Error while setting up the request
      console.error('Request setup error:', error.message);
    }

    throw error; // Propagate error for further handling
  }
};
