import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom'; // Import useNavigate
import { setUserId } from '../Redux/userSlice'; // Import setUserId action

const Login = () => {
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const dispatch = useDispatch();
  const navigate = useNavigate(); // Hook to navigate after login

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/api/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId: userId, password }), // userId comes from Redux
      });

      // Check if the response is successful
      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`Server error: ${response.status}, ${errorText}`);
      }

      const data = await response.json();

      if (data.success) {
        dispatch(setUserId(userId)); // Store userId in Redux after successful login
        setMessage(data.message);
        navigate('/home'); // Navigate to home page after login
      } else {
        setMessage(data.message);
      }
    } catch (error) {
      setMessage('Error connecting to the server.');
      console.error(error);
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div className="input-group">
          <label>UserId:</label>
          <input
            type="text"
            value={userId}
            onChange={(e) => dispatch(setUserId(e.target.value))} // Dispatch onChange to store userId in Redux
            required
          />
        </div>
        <div className="input-group">
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Login</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default Login;
