import React, { useState, useEffect } from "react";
import { getColorMatchingByRSN } from "../API/ColorApi";

const ShowColorMatching = ({ RSN }) => {
  const [colorMatchingData, setColorMatchingData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    if (RSN) {
      const fetchData = async () => {
        setLoading(true);
        try {
          const response = await getColorMatchingByRSN(RSN);
          setColorMatchingData(response.data); // Assuming the response contains color matching details
        } catch (err) {
          setError("Failed to fetch color matching details.");
        } finally {
          setLoading(false);
        }
      };

      fetchData();
    }
  }, [RSN]); // Fetch when RSN changes

  if (loading) return <p>Loading...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  // Group data by MatchingName
  const groupedData = colorMatchingData?.reduce((acc, item) => {
    const { MatchingName } = item;
    if (!acc[MatchingName]) {
      acc[MatchingName] = [];
    }
    acc[MatchingName].push(item);
    return acc;
  }, {});

  return (
    <div>
      <h1>Color Matching Details</h1>
      {groupedData ? (
        Object.keys(groupedData).map((matchingName) => (
          <div key={matchingName}>
            <h2>Matching Name: {matchingName}</h2>
            <table border="1" style={{ width: "100%", marginTop: "20px" }}>
              <thead>
                <tr>
                  <th>ColorID</th>
                  <th>Panel</th>
                </tr>
              </thead>
              <tbody>
                {groupedData[matchingName].map((item, index) => (
                  <tr key={index}>
                    <td>{item.ColorId}</td>
                    <td>{item.Panel}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ))
      ) : (
        <p>No color matching details available for this RSN.</p>
      )}
    </div>
  );
};

export default ShowColorMatching;
