// /frontend/api/api.js
import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

export const AddSampleDetails = async (data) => {
  try {
    const response = await axios.post(`${API_URL}/addSample`, data, {
      headers: { 'Content-Type': 'application/json' }
    });

    console.log(response.data)
    return response.data;
  } catch (error) {
    console.error('API call error:', error);  // Log any network error
    throw new Error('Error adding Sample Details');
  }
};
