import React, { useState, useEffect } from "react";
import { getKnittingDetailsByRSN } from "../API/SampleApi";

const ShowKnittingDetails = ({ RSN }) => {
  const [knittingData, setKnittingData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    if (RSN) {
      const fetchData = async () => {
        setLoading(true);
        try {
          const response = await getKnittingDetailsByRSN(RSN);
          setKnittingData(response.data); // Assuming the response contains knitting details
        } catch (err) {
          setError("Failed to fetch knitting details.");
        } finally {
          setLoading(false);
        }
      };

      fetchData();
    }
  }, [RSN]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  // Filter out the fields that have null or empty values
  const nonNullData = knittingData
    ? Object.entries(knittingData).filter(
        ([key, value]) => value != null && value !== ""
      )
    : [];

  return (
    <div>
      <h1>Knitting Details</h1>
      {nonNullData.length > 0 ? (
        <table border="1" style={{ width: "100%", marginTop: "20px" }}>
          <thead>
            <tr>
              <th>Field</th>
              <th>Weight</th>
              <th>Time</th>
            </tr>
          </thead>
          <tbody>
            {nonNullData.map(([key, value]) => {
              // Check if the value is an object with Weight and Time properties
              if (typeof value === 'object' && value !== null) {
                return (
                  <tr key={key}>
                    <td>{key}</td>
                    <td>{value.Weight || 'N/A'}</td> {/* Render Weight */}
                    <td>{value.Time || 'N/A'}</td> {/* Render Time */}
                  </tr>
                );
              }
              return (
                <tr key={key}>
                  <td>{key}</td>
                  <td colSpan="2">{value}</td> {/* Render non-object values */}
                </tr>
              );
            })}
          </tbody>
        </table>
      ) : (
        <p>No data available</p>
      )}
    </div>
  );
};

export default ShowKnittingDetails;
