import React, { useState, useEffect } from "react";
import { getSampleDetailsByRSN } from "../API/SampleApi";

const ShowKnittingDetails = ({ RSN }) => {
  const [sampleDetails, setSampleDetails] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  // Fetch data when RSN changes
  useEffect(() => {
    if (RSN) {
      const fetchData = async () => {
        setLoading(true);
        try {
          const response = await getSampleDetailsByRSN(RSN);
          setSampleDetails(response.data); // Assuming the response has a "data" field
        } catch (err) {
          setError("Failed to fetch sample details.");
        } finally {
          setLoading(false);
        }
      };
      fetchData();
    }
  }, [RSN]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  // Filter non-null data
  const nonNullDetails = sampleDetails
    ? Object.entries(sampleDetails).filter(([key, value]) => value != null)
    : [];

  return (
    <div>
      {nonNullDetails.length > 0 ? (
        <table border="1" style={{ width: "100%", marginTop: "20px" }}>
          <thead>
            <tr>
              <th>Field</th>
              <th>Value</th>
            </tr>
          </thead>
          <tbody>
            {nonNullDetails.map(([key, value]) => (
              <tr key={key}>
                <td>{key}</td>
                <td>{value}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No data available</p>
      )}
    </div>
  );
};

export default ShowKnittingDetails;
