import React, { useState, useEffect } from "react";
import { getYarnUsageByArticleNo } from "../API/YarnUsageApi";

const AddOrderDetails = ({ ArticleNo }) => {
  const [yarnUsageData, setYarnUsageData] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [quantities, setQuantities] = useState({});

  useEffect(() => {
    const fetchYarnUsage = async () => {
      try {
        const response = await getYarnUsageByArticleNo(ArticleNo);
        if (response.success && response.data && response.data.length > 0) {
          setYarnUsageData(response.data);
        } else {
          setError("No data found for this ArticleNo.");
        }
      } catch (err) {
        setError("Failed to fetch yarn usage data.");
      } finally {
        setLoading(false);
      }
    };

    fetchYarnUsage();
  }, [ArticleNo]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  // List of all possible sizes (to ensure we check across all data)
  const sizes = [
    "Freesize",
    "XS",
    "S",
    "M",
    "L",
    "XL",
    "2XL",
    "3XL",
    "4XL",
    "5XL",
  ];

  // Function to get the sizes that have non-null values across all MatchingNames
  const getAllNonNullSizes = (data) => {
    const nonNullSizes = sizes.filter((size) =>
      data.some((row) => row[size] !== null)
    );
    return nonNullSizes;
  };

  // Get all non-null sizes across all yarn usage data
  const nonNullSizes = getAllNonNullSizes(yarnUsageData);

  // Function to handle quantity input changes
  const handleQuantityChange = (matchingName, size, value) => {
    setQuantities((prevQuantities) => ({
      ...prevQuantities,
      [matchingName]: {
        ...prevQuantities[matchingName],
        [size]: value,
      },
    }));
  };

  // Function to get non-null yarns (YarnId and Weight)
  const getNonNullYarns = (data) => {
    const yarns = [];
    for (let i = 1; i <= 15; i++) {
      const yarnKey = `Yarn${i}`;
      if (data[yarnKey] && data[yarnKey].YarnId) {
        yarns.push({
          YarnId: data[yarnKey].YarnId,
          Weight: data[yarnKey].Weight,
        });
      }
    }
    return yarns;
  };

  // Function to calculate the updated weight based on the quantity change
  const calculateUpdatedWeight = (originalWeight, size, matchingName) => {
    const quantity = quantities[matchingName]?.[size];
    const percentage = yarnUsageData.find(
      (row) => row.MatchingName === matchingName
    )[size]; // Assuming the percentage data is part of the row

    if (quantity && percentage !== null) {
      const percentageAdjustment = 1 + percentage / 100;
      return originalWeight * percentageAdjustment;
    }
    return originalWeight; // No change if no quantity or percentage
  };

  return (
    <div>
      <h2>
        Order Quantity Details for Article No: {ArticleNo} with RSN:{" "}
        {yarnUsageData[0]?.RSN}
      </h2>

      {/* Table for Yarn Quantity Inputs */}
      {yarnUsageData.length > 0 ? (
        <table
          border="1"
          cellPadding="10"
          style={{ width: "100%", marginTop: "20px" }}
        >
          <thead>
            <tr>
              <th>Matching Name</th>
              {nonNullSizes.map((size) => (
                <th key={size}>{size}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {yarnUsageData.map((data, dataIndex) => (
              <tr key={dataIndex}>
                <td>{data.MatchingName}</td>
                {nonNullSizes.map((size) => (
                  <td key={size}>
                    <input
                      type="number"
                      value={quantities[data.MatchingName]?.[size] || ""}
                      onChange={(e) =>
                        handleQuantityChange(
                          data.MatchingName,
                          size,
                          e.target.value
                        )
                      }
                      min="0"
                    />
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <div>No yarn usage data found.</div>
      )}

      {/* Table for YarnId and Weight */}
      {yarnUsageData.length > 0 && (
        <div style={{ marginTop: "30px" }}>
          <h2>Yarn Details</h2>
          <table
            border="1"
            cellPadding="10"
            style={{ width: "100%", marginTop: "20px" }}
          >
            <thead>
              <tr>
                <th>Matching Name</th>
                <th>Yarn ID</th>
                <th>Weight</th>
                {nonNullSizes.map((size) => (
                  <th key={size}>{size}</th>
                ))}
                <th>Total</th>
              </tr>
            </thead>
            <tbody>
              {yarnUsageData.map((data, dataIndex) => {
                const yarns = getNonNullYarns(data);
                return yarns.map((yarn, yarnIndex) => (
                  <tr key={`${dataIndex}-${yarnIndex}`}>
                    {yarnIndex === 0 && (
                      <td rowSpan={yarns.length}>{data.MatchingName}</td>
                    )}
                    <td>{yarn.YarnId}</td>
                    <td>{calculateUpdatedWeight(yarn.Weight, 'S', data.MatchingName).toFixed(2)}</td>
                    {/* Display Yarn Details for non-null sizes */}
                    {nonNullSizes.map((size) => (
                      <td key={`${size}-${yarnIndex}`}>
                        {data[size] !== null ? `${data[size]}%` : "-"}
                      </td>
                    ))}
                    <td></td>
                  </tr>
                ));
              })}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default AddOrderDetails;
