import React, { useState, useEffect } from "react";
import { getKnittingDetailsByRSN } from "../API/SampleApi";

const ShowKnittingDetails = ({ RSN }) => {
  const [knittingData, setKnittingData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    if (RSN) {
      const fetchData = async () => {
        setLoading(true);
        try {
          const response = await getKnittingDetailsByRSN(RSN);
          console.log("Fetched Data:", response);
          
          // Check if response is an array or object and handle accordingly
          setKnittingData(Array.isArray(response) ? response : [response]); // Wrap single object in an array if necessary
        } catch (err) {
          setError("Failed to fetch knitting details.");
        } finally {
          setLoading(false);
        }
      };

      fetchData();
    }
  }, [RSN]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  // Function to check for non-null, non-zero, and non-empty values
  const isNonZero = (value) => {
    if (typeof value === "object" && value !== null) {
      return (
        (value.Weight && value.Weight !== 0) || // Weight is non-zero
        (value.Time && value.Time !== 0) // Time is non-zero
      );
    }
    return value !== 0 && value !== "" && value !== null && value !== undefined;
  };

  // Function to filter out fields with null, zero, or empty values
  const filterData = (data) => {
    return Object.entries(data).filter(([key, value]) => isNonZero(value));
  };

  return (
    <div>
      <h1>Knitting Details</h1>
      {knittingData.length > 0 ? (
        knittingData.map((data, index) => {
          const nonZeroData = filterData(data);
          return (
            <div key={index} style={{ marginTop: "30px" }}>
              <h2>Knitting Record {index + 1}</h2>
              {nonZeroData.length > 0 ? (
                <table border="1" style={{ width: "100%" }}>
                  <thead>
                    <tr>
                      <th>Field</th>
                      <th>Weight</th>
                      <th>Time</th>
                    </tr>
                  </thead>
                  <tbody>
                    {nonZeroData.map(([key, value]) => {
                      if (typeof value === "object" && value !== null) {
                        return (
                          <tr key={key}>
                            <td>{key}</td>
                            <td>{value.Weight || "N/A"}</td> {/* Render Weight */}
                            <td>{value.Time || "N/A"}</td> {/* Render Time */}
                          </tr>
                        );
                      }
                      return (
                        <tr key={key}>
                          <td>{key}</td>
                          <td colSpan="2">{value || "N/A"}</td> {/* Render non-object values */}
                        </tr>
                      );
                    })}
                  </tbody>
                </table>
              ) : (
                <p>No relevant data available for this record.</p>
              )}
            </div>
          );
        })
      ) : (
        <p>No knitting details available</p>
      )}
    </div>
  );
};

export default ShowKnittingDetails;
