import React, { useState, useEffect } from 'react';
import { getImageByRSN } from '../API/ImageApi';

const ShowImage = ({ RSN }) => {
  const [imageData, setImageData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  console.log(typeof(RSN));
  const rsn = Number(RSN);
  console.log(typeof(rsn));
  useEffect(() => {
    const fetchImage = async () => {
      try {
        setLoading(true);
        setError(null);
        const data = await getImageByRSN(RSN);
        console.log('Image data received:', data); // Debugging log
        setImageData(data.ImageData); // Assuming the data contains ImageData (base64 or file path)
      } catch (err) {
        setError('Error fetching image');
        console.error(err); // Log the error for debugging
      } finally {
        setLoading(false);
      }
    };

    fetchImage();
  }, [RSN]);

  if (loading) {
    return <p>Loading image...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  return (
    <div>
      {/* {imageData ? (
        // Check if it's base64 or file path before displaying
        imageData.startsWith('data:image') ? (
          <img src={imageData} alt="Sample" style={{ width: '100%', maxWidth: '600px' }} />
        ) : (
          // If it's a file path, use a proper URL to fetch the image
          <img src={`http://localhost:5000/uploads/${imageData}`} alt="Sample" style={{ width: '100%', maxWidth: '600px' }} />
        )
      ) : (
        <p>No image found for this RSN.</p>
      )} */}
    </div>
  );
};

export default ShowImage;
