import React, { useState, useEffect } from "react";
import { getYarnUsageByArticleNo } from "../API/YarnUsageApi";

const AddOrderDetails = ({ ArticleNo }) => {
  const [yarnUsageData, setYarnUsageData] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchYarnUsage = async () => {
      try {
        const response = await getYarnUsageByArticleNo(ArticleNo);
        if (response.success && response.data && response.data.length > 0) {
          setYarnUsageData(response.data[0]);
        } else {
          setError("No data found for this ArticleNo.");
        }
      } catch (err) {
        setError("Failed to fetch yarn usage data.");
      } finally {
        setLoading(false);
      }
    };

    fetchYarnUsage();
  }, [articleNo]);

  if (loading) {
    return <div>Loading...</div>;
  }

  // Show error if any
  if (error) {
    return <div>{error}</div>;
  }

  // Display the yarn usage data in a structured format
  return (
    <div>
      <h2>Yarn Usage Details for Article No: {articleNo}</h2>
      {yarnUsageData && (
        <div>
          <p><strong>RSN:</strong> {yarnUsageData.RSN}</p>
          <p><strong>Matching Name:</strong> {yarnUsageData.MatchingName}</p>
          <p><strong>User ID:</strong> {yarnUsageData.UserId}</p>

          <div>
            <h3>Yarn Details:</h3>
            <ul>
              {Array.from({ length: 15 }).map((_, index) => {
                const yarnField = `Yarn${index + 1}`;
                const yarn = yarnUsageData[yarnField];

                if (yarn) {
                  const { YarnId, Weight } = JSON.parse(yarn);
                  return (
                    <li key={index}>
                      <strong>Yarn {index + 1}:</strong>
                      <p>Yarn ID: {YarnId}</p>
                      <p>Weight: {Weight}</p>
                    </li>
                  );
                }
                return null;
              })}
            </ul>
          </div>
        </div>
      )}
    </div>
  );
};

export default AddOrderDetails;
