import React, { useEffect, useState } from "react";
import { sampleList } from "../API/SampleApi";

const SampleList = () => {
  const [samples, setSamples] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchSampleList = async () => {
      try {
        const data = await sampleList();
        setSamples(data); 
      } catch (err) {
        setError("Failed to load sample list");
      } finally {
        setLoading(false);
      }
    };

    fetchSampleList();
  }, []); 

  if (loading) return <p>Loading...</p>; 
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  console.log(samples);
  return (
    <div>
      <h2>Sample List</h2>
      {samples.length > 0 ? (
        <table border="1" cellPadding="10" style={{ width: "100%", marginTop: "20px" }}>
          <thead>
            <tr>
              <th>RSN</th>
              <th>ArticleNo</th>
              <th>Total Sample Weight</th>
              <th>Total Knitting Time</th>
              <th>Image</th>
            </tr>
          </thead>
          <tbody>
            {samples.map((sample) => (
              <tr key={sample.id}>
                <td>{sample.id}</td>
                <td>{sample.name}</td>
                <td>{new Date(sample.createdAt).toLocaleString()}</td>
                <td>
                  <button>Back</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No samples found.</p>
      )}
    </div>
  );
};

export default SampleList;
