import React, { useState } from 'react';
import { addKnittingDetails } from '../API/Api'

const AddKnittingDetails = () => {
  const [formData, setFormData] = useState({
    RSN: '',
    Size: '',
    FrontRight: { weight: '', time: '' },
    FrontLeft: { weight: '', time: '' },
    FrontComplete: { weight: '', time: '' },
    BackRight: { weight: '', time: '' },
    BackLeft: { weight: '', time: '' },
    BackComplete: { weight: '', time: '' },
    SleeveRight: { weight: '', time: '' },
    SleeveLeft: { weight: '', time: '' },
    BothSleeves: { weight: '', time: '' },
    Tape: { weight: '', time: '' },
    Collar: { weight: '', time: '' },
    Kharcha1: { weight: '', time: '' },
    Kharcha2: { weight: '', time: '' },
    Kharcha3: { weight: '', time: '' },
    Total: { weight: '', time: '' },
  });

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  const handleChange = (e, field, type) => {
    const { value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [field]: {
        ...prevData[field],
        [type]: value,
      },
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate that all fields have both weight and time
    for (let field in formData) {
      if (typeof formData[field] === 'object') {
        const { weight, time } = formData[field];
        if (weight === '' || time === '') {
          setError(`Both weight and time must be provided for ${field}`);
          return; // Stop submission if any required field is missing
        }
      }
    }

    // Start loading indicator
    setLoading(true);
    setError(null);
    setSuccess(null);

    try {
      // Send the data to the backend via the API function
      const response = await addKnittingDetails(formData);
      
      // On success, display success message and clear form
      setSuccess('Knitting details added successfully!');
      setFormData({
        RSN: '',
        Size: '',
        FrontRight: { weight: '', time: '' },
        FrontLeft: { weight: '', time: '' },
        FrontComplete: { weight: '', time: '' },
        BackRight: { weight: '', time: '' },
        BackLeft: { weight: '', time: '' },
        BackComplete: { weight: '', time: '' },
        SleeveRight: { weight: '', time: '' },
        SleeveLeft: { weight: '', time: '' },
        BothSleeves: { weight: '', time: '' },
        Tape: { weight: '', time: '' },
        Collar: { weight: '', time: '' },
        Kharcha1: { weight: '', time: '' },
        Kharcha2: { weight: '', time: '' },
        Kharcha3: { weight: '', time: '' },
        Total: { weight: '', time: '' },
      });
    } catch (error) {
      // Handle errors if the API request fails
      setError(error.message || 'An error occurred while adding knitting details');
    } finally {
      // End loading indicator
      setLoading(false);
    }
  };

  return (
    <div>
      <h1>Add Knitting Details</h1>
      <form onSubmit={handleSubmit}>
        <table border="1">
          <thead>
            <tr>
              <th>Field</th>
              <th>Weight</th>
              <th>Time</th>
            </tr>
          </thead>
          <tbody>
            {/* Static Fields (RSN and Size) */}
            <tr>
              <td>RSN:</td>
              <td colSpan="2">
                <input
                  type="text"
                  name="RSN"
                  value={formData.RSN}
                  onChange={(e) => setFormData({ ...formData, RSN: e.target.value })}
                  required
                />
              </td>
            </tr>

            <tr>
              <td>Size:</td>
              <td colSpan="2">
                <input
                  type="text"
                  name="Size"
                  value={formData.Size}
                  onChange={(e) => setFormData({ ...formData, Size: e.target.value })}
                  required
                />
              </td>
            </tr>

            {/* JSON Fields */}
            {[
              'FrontRight', 'FrontLeft', 'FrontComplete', 'BackRight', 'BackLeft',
              'BackComplete', 'SleeveRight', 'SleeveLeft', 'BothSleeves', 'Tape',
              'Collar', 'Kharcha1', 'Kharcha2', 'Kharcha3', 'Total'
            ].map((field) => (
              <tr key={field}>
                <td>{field}</td>
                <td>
                  <input
                    type="number"
                    value={formData[field].weight}
                    onChange={(e) => handleChange(e, field, 'weight')}
                    placeholder="Weight"
                    required
                  />
                </td>
                <td>
                  <input
                    type="number"
                    value={formData[field].time}
                    onChange={(e) => handleChange(e, field, 'time')}
                    placeholder="Time"
                    required
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        <button type="submit" disabled={loading}>
          {loading ? 'Submitting...' : 'Submit'}
        </button>
      </form>

      {/* Display Success or Error Messages */}
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {success && <p style={{ color: 'green' }}>{success}</p>}
    </div>
  );
};

export default AddKnittingDetails;
