// src/api/ImageApi.js
import axios from 'axios';

// Set base URL for API (adjust as needed)
const API_URL = 'http://localhost:5000'; // Change to your server URL

// Function to upload an image
export const uploadImage = async (imageData, imageName) => {
  const formData = new FormData();
  formData.append('image', imageData); // File data
  formData.append('imageName', imageName); // Image name

  try {
    const response = await axios.post(`${API_URL}/upload-image`, formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    });
    return response.data;
  } catch (error) {
    console.error("Error uploading image:", error);
    throw error;
  }
};

// Function to fetch image by RSN
export const getImageByRSN = async (RSN) => {
  try {
    const response = await axios.get(`${API_URL}/image/${RSN}`);
    return response.data;
  } catch (error) {
    console.error("Error fetching image:", error);
    throw error;
  }
};
