import React, { useState } from "react";
import { getOrderDetails } from "../API/OrderApi";

const SearchOrder = () => {
  // State to hold the order number, order details, and any errors
  const [orderNo, setOrderNo] = useState("");
  const [orderDetails, setOrderDetails] = useState(null);
  const [error, setError] = useState(null);

  // Handler for fetching order details
  const fetchOrderDetails = async () => {
    if (!orderNo) {
      setError("Please enter an order number");
      return;
    }

    try {
      setError(null); // Clear any previous error
      const response = await getOrderDetails(orderNo);
      if (response) {
        setOrderDetails(response);
      } else {
        setError("No order details found");
      }
    } catch (err) {
      setError("An error occurred while fetching the order details");
      console.error(err);
    }
  };

  return (
    <div>
      <h2>Search Order</h2>
      <div>
        <input
          type="text"
          placeholder="Enter Order No"
          value={orderNo}
          onChange={(e) => setOrderNo(e.target.value)}
        />
        <button onClick={fetchOrderDetails}>Search</button>
      </div>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {orderDetails && (
        <div>
          <h3>Order Details</h3>
          <pre>{JSON.stringify(orderDetails, null, 2)}</pre>
        </div>
      )}
    </div>
  );
};

export default SearchOrder;
