import React, { useState, useEffect } from 'react';
import { employeeDetails } from '../API/EmployeeApi';

const EmployeeDetails = () => {
  // State hooks to manage employees, loading state, and error state
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch employee details on component mount
  useEffect(() => {
    const fetchEmployeeData = async () => {
      try {
        const data = await employeeDetails(); // Fetch employee details
        setEmployees(data.data); // Set the employees' data to state
        setLoading(false); // Set loading to false once data is fetched
      } catch (err) {
        setError('Failed to fetch employee details'); // Handle error
        setLoading(false); // Stop loading if there's an error
      }
    };

    fetchEmployeeData(); // Call the function to fetch data
  }, []); // Empty dependency array means this effect runs once on mount

  // If loading, show loading indicator
  if (loading) return <div>Loading...</div>;

  // If there's an error, show the error message
  if (error) return <div>{error}</div>;

  // If data is successfully fetched, display the employee list
  return (
    <div>
      <h1>Employee List</h1>
      <ul>
        {employees.map((employee) => (
          <li key={employee.id}>
            {employee.name} - {employee.position} {/* Adjust these properties to match your data structure */}
          </li>
        ))}
      </ul>

      <table>
        <thead>
          <tr>
            <th>RSN</th>
            <th>ArticleName</th>
            <th>DesignFileNo</th>
            <th>SeriesArticleFileNo</th>
            <th>ArticleType</th>
            <th>Gender</th>
            <th>MachineSpeed</th>
            <th>Designer</th>
            <th>Grapher</th>
            <th>SampleMaster</th>
            <th>SampleStatus</th>
          </tr>
        </thead>
        <tbody>
          {Array.isArray(sampleDetails) && sampleDetails.map((sample) => (  // Ensure sampleDetails is an array
            <tr key={sample.RSN}>
              <td>{sample.RSN}</td>
              <td>{sample.ArticleName}</td>
              <td>{sample.DesignFileNo}</td>
              <td>{sample.SeriesArticleFileNo}</td>
              <td>{sample.ArticleType}</td>
              <td>{sample.Gender}</td>
              <td>{sample.MachineSpeed}</td>
              <td>{sample.Designer}</td>
              <td>{sample.Grapher}</td>
              <td>{sample.SampleMaster}</td>
              <td>{sample.SampleStatus}</td>
            </tr>
          ))}
          {!Array.isArray(sampleDetails) && <p>Not an Array</p>}
        </tbody>
        </table>
    </div>
  );
};

export default EmployeeDetails;
