import React, { useState, useEffect } from "react";
import { addSampleDetails } from "../API/SampleApi";
import { useNavigate } from "react-router-dom"; // Import useNavigate from react-router-dom
import { getRSN } from "../API/SampleApi"; // Assuming you have an API function to fetch the RSN

const AddSampleDetails = () => {
  const [formData, setFormData] = useState({
    articleName: "",
    designFileNo: "",
    seriesArticleFileNo: "",
    articleType: "",
    gender: "",
    machineSpeed: "",
    designer: "",
    grapher: "",
    master: "",
    sampleStatus: "",
  });

  const [message, setMessage] = useState(null);
  const [loading, setLoading] = useState(false);
  const [RSN, setRSN] = useState(null); // State to store RSN
  const navigate = useNavigate();  // Initialize the navigate function

  useEffect(() => {
    // Fetch the RSN when the component loads
    const fetchRSN = async () => {
      try {
        const fetchedRSN = await getRSN();  // Assuming getRSN is an API function
        setRSN(fetchedRSN); // Set the fetched RSN
      } catch (error) {
        setMessage("Error fetching RSN.");
      }
    };

    fetchRSN();
  }, []);  // Empty dependency array means this effect runs once on component mount

  const handleChange = (e) => {
    const { id, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [id]: value,
    }));
  };

  const handleMachineSpeedChange = (e) => {
    const value = e.target.value;
    if (!isNaN(value)) {
      setFormData((prevData) => ({
        ...prevData,
        machineSpeed: value,
      }));
    }
  };

  const validateForm = () => {
    const {
      articleName,
      designFileNo,
      seriesArticleFileNo,
      articleType,
      gender,
      machineSpeed,
      designer,
      grapher,
      master,
      sampleStatus,
    } = formData;

    return (
      articleName &&
      designFileNo &&
      seriesArticleFileNo &&
      articleType &&
      gender &&
      machineSpeed &&
      designer &&
      grapher &&
      master &&
      sampleStatus &&
      RSN // Ensure RSN is fetched
    );
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      setMessage("All fields are required.");
      return;
    }

    const { articleName, designFileNo, seriesArticleFileNo, articleType, gender, machineSpeed, designer, grapher, master, sampleStatus } = formData;

    const data = {
      articleName,
      designFileNo,
      seriesArticleFileNo,
      articleType,
      gender,
      machineSpeed,
      designer,
      grapher,
      master,
      sampleStatus,
      RSN,  // Include RSN in the data sent to the backend
    };

    try {
      setLoading(true);
      const result = await addSampleDetails(data);  // Assuming this adds sample data to the backend
      setMessage(result.message || "Sample details added successfully.");

      // Redirect to the Panel Selection page and pass the RSN to the next page
      navigate("/panel-selection", { state: { RSN } });  // Use navigate to pass RSN to next page
    } catch (error) {
      setMessage(error.message || "An error occurred while adding the sample details.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <div>
        <h2>Add Sample Details</h2>
        <form onSubmit={handleSubmit}>
          {Object.keys(formData).map((key) => (
            <div key={key}>
              <label htmlFor={key}>{key.replace(/([A-Z])/g, " $1").toUpperCase()}:</label>
              <input
                id={key}
                type={key === "machineSpeed" ? "number" : "text"}
                value={formData[key]}
                onChange={key === "machineSpeed" ? handleMachineSpeedChange : handleChange}
                required
              />
            </div>
          ))}
          <button type="submit" disabled={loading}>
            Add New
          </button>
        </form>
        {loading && <p>Loading...</p>}
        {message && <p>{message}</p>}
      </div>
    </div>
  );
};

export default AddSampleDetails;
