import React, { useState } from 'react';
import { uploadImage } from '../API/ImageApi';
import { useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';

const AddImage = () => {
  const [RSN, setRSN] = useState('');
  const [image, setImage] = useState(null);
  const [imageName, setImageName] = useState('');
  const [uploadStatus, setUploadStatus] = useState('');

  const userId = useSelector((state) => state.user.userId);
  const navigate = useNavigate(); // Initialize useNavigate for navigation

  // Handle image file selection
  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
  };

  // Handle form submit (upload image)
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!image || !imageName || !RSN) {
      alert("Please provide an image, a name, and RSN.");
      return;
    }

    setUploadStatus("Uploading...");

    try {
      // Ensure RSN is a number before sending it
      const numericRSN = parseInt(RSN, 10);
      if (isNaN(numericRSN)) {
        throw new Error("Invalid RSN entered.");
      }

      // Upload the image and pass RSN, imageName, image data, and userId
      const result = await uploadImage(image, imageName, numericRSN, userId);
      setUploadStatus(`Image uploaded successfully! RSN: ${result.RSN}`);
    } catch (error) {
      setUploadStatus(`Error uploading image: ${error.message}`);
    }
  };

  // Function to handle navigation to '/add-sample-details'
  const handleNext = () => {
    navigate(`/panel-selection/${result.RSN}`, {
      state: { RSN: RSN, action: "Add" },
    });
  };

  return (
    <div>
      <h2>Upload an Image</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="RSN">RSN</label>
          <input
            type="number"
            id="RSN"
            value={RSN}
            onChange={(e) => setRSN(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="imageName">Image Name</label>
          <input
            type="text"
            id="imageName"
            value={imageName}
            onChange={(e) => setImageName(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="image">Select Image</label>
          <input
            type="file"
            id="image"
            onChange={handleImageChange}
            accept="image/*"
            required
          />
        </div>
        <button type="submit">Upload Image</button>
      </form>

      {uploadStatus && <p>{uploadStatus}</p>}

      {/* Button to navigate to '/add-sample-details' */}
      <div>
        <button onClick={handleNext}>Next</button>
      </div>
    </div>
  );
};

export default AddImage;
