import React, { useState, useEffect } from "react";
import { getKnittingDetailsByRSN } from "../API/SampleApi";

const ShowKnittingDetails = ({ RSN }) => {
  const [knittingData, setKnittingData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    if (RSN) {
      const fetchData = async () => {
        setLoading(true);
        try {
          const response = await getKnittingDetailsByRSN(RSN);
          console.log("Fetched Knitting Data:", response);  // Log full response
          const data = response.data;
          
          // Check if the 'data' field is not an empty object
          if (data && Object.keys(data).length > 0) {
            setKnittingData(data); // Set knitting data to state if it's valid
          } else {
            setKnittingData([]); // If no data, set as empty array
          }
        } catch (err) {
          setError("Failed to fetch knitting details.");
        } finally {
          setLoading(false);
        }
      };

      fetchData();
    }
  }, [RSN]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  // Function to check if the field has non-zero or non-null values
  const isNonZero = (value) => {
    if (value && typeof value === "object") {
      return (
        (value.Weight && value.Weight !== 0) ||
        (value.Time && value.Time !== 0) ||
        (value.MachineNo && value.MachineNo !== "")
      );
    }
    return value !== 0 && value !== "" && value !== null && value !== undefined;
  };

  // If knittingData is an object, process each field inside the object
  const nonZeroData = knittingData
    ? Object.entries(knittingData).filter(([key, value]) => isNonZero(value))
    : [];

  if (nonZeroData.length === 0) {
    return <p>No relevant data available</p>;
  }

  return (
    <div>
      <h1>Knitting Details</h1>
      {/* Iterate over the non-zero data to create separate tables for each field */}
      {nonZeroData.map(([key, value], index) => {
        return (
          <div key={index} style={{ marginBottom: "20px" }}>
            <h2>{key}</h2>
            <table border="1" style={{ width: "100%" }}>
              <thead>
                <tr>
                  <th>Field</th>
                  <th>Time</th>
                  <th>Weight</th>
                  <th>Machine No.</th>
                </tr>
              </thead>
              <tbody>
                {/* Check if the value is an object */}
                {typeof value === "object" && value !== null ? (
                  <tr>
                    <td>{key}</td>
                    <td>{value.Time || "N/A"}</td> {/* Render Time */}
                    <td>{value.Weight || "N/A"}</td> {/* Render Weight */}
                    <td>{value.MachineNo || "N/A"}</td> {/* Render MachineNo */}
                  </tr>
                ) : (
                  <tr>
                    <td>{key}</td>
                    <td colSpan="3">{value}</td> {/* Render non-object values */}
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        );
      })}
    </div>
  );
};

export default ShowKnittingDetails;
