import React, { useState } from "react";
import { addYarnDetails } from "../API/YarnApi";

const AddYarnDetails = () => {
    const [yarnId, setYarnId] = useState("");
  const [yarnType, setYarnType] = useState("Vislon");
  const [yarnCount, setYarnCount] = useState("2/48");
  const [colorName, setColorName] = useState("Red");
  const [ColorCode, setColorCode] = useState("06401");
  const [message, setMessage] = useState(null);
  const [loading, setLoading] = useState(false);

  const createYarnId = () => {
    const createId = yarnType.charAt(0) + yarnCount.charAt(yarnCount.length-2) + colorName.charAt(0) + colorName.charAt(colorName.length - 1) + ColorCode;
    return createId;
  }

  setYarnId(createYarnId);
  return (
    <div>yarnId</div>
  )
};

export default AddYarnDetails;
