import React, { useState, useEffect } from "react";
import { getColorMatchingByRSN } from "../API/ColorApi";

const ShowColorMatching = ({ RSN }) => {
  const [colorMatchingData, setColorMatchingData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    if (RSN) {
      const fetchData = async () => {
        setLoading(true);
        try {
          const response = await getColorMatchingByRSN(RSN);
          setColorMatchingData(response.data); // Assuming the response contains color matching details
        } catch (err) {
          setError("Failed to fetch color matching details.");
        } finally {
          setLoading(false);
        }
      };

      fetchData();
    }
  }, [RSN]); // Fetch when RSN changes

  if (loading) return <p>Loading...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div>
      <h1>Color Matching Details</h1>
      {colorMatchingData ? (
        <div>
          <h2>Matching for RSN: {RSN}</h2>
          {/* Assuming the data is an array of matching color details */}
          <table border="1" style={{ width: "100%", marginTop: "20px" }}>
            <thead>
              <tr>
                <th>ColorID</th>
                <th>Matching Name</th>
                <th>Panel</th>
              </tr>
            </thead>
            <tbody>
              {colorMatchingData.map((item, index) => (
                <tr key={index}>
                  <td>{item.ColorId}</td>
                  <td>{item.MatchingName}</td>
                  <td>{item.Panel}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <p>No color matching details available for this RSN.</p>
      )}
    </div>
  );
};

export default ShowColorMatching;
