import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

export const addSampleDetails = async (data) => {

    console.log(data);

  try {
    const response = await axios.post(`${API_URL}/add-sample`, data, {
      headers: { 'Content-Type': 'application/json' }
    });

    console.log("alisjkjsnmjaksnx")
    return response.data;
  } catch (error) {
    console.error('API call error:', error);  // Log any network error
    throw new Error('Error adding Sample Details');
  }
};
