import { useState } from "react";
import React from "react";
import AddSampleDetails from "../Components/AddSampleDetails";
import SampleDetails from "../Components/SampleDetails";
import UpdateSampleData from "../Components/UpdateSample";

const WelcomeSample = () =>{
    const [selectedComponent, setSelectedComponent] = useState('');

    const handleButtonClick = (component) => {
        setSelectedComponent(component);
    };

    const renderComponent = () => {
        switch (selectComponent) {
            case 'ShowAll':
                return <SampleDetails />;
            
            case 'NewRecord':
                return <AddSampleDetails />;

            case 'UpdateRecord':
                return <UpdateSampleData />;

            default:
                return <p>Please select the activity you want to perform.</p>
        }
    };

    return (
        <div>
        <div>
            <h1>Welcome to Sample Details Form</h1>
            <button onClick={() => handleButtonClick('ShowAll')}>Show All Sample Details</button>
            <button onClick={() => handleButtonClick('NewRecord')}>Add New Sample Record</button>
            <button onClick={() => handleButtonClick('UpdateRecord')}>Update Existing Sample Record</button>
        </div>
        <div>
        {renderComponent()}
      </div>
        </div>
    )
};

export default WelcomeSample;
