import React, { useState, useEffect } from "react";
import { getColorMatchingByRSN } from "../API/ColorApi";  // Function to fetch color matching data
import ShowColorDetail from "./ShowColorDetails";  // Import ShowColorDetail to display color details

const ShowColor = ({ RSN }) => {
  const [colorMatchingData, setColorMatchingData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchColorMatchingData = async () => {
      setLoading(true);
      try {
        const response = await getColorMatchingByRSN(RSN);
        setColorMatchingData(response); 
      } catch (err) {
        setError("Failed to fetch color matching details.");
      } finally {
        setLoading(false);
      }
    };

    if (RSN) {
      fetchColorMatchingData();
    }
  }, [RSN]);

  if (loading) return <p>Loading color matching data...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div>
      <h1>Color Matching Details for RSN: {RSN}</h1>
      {colorMatchingData ? (
        <div>
          {/* Displaying color matching details */}
          {colorMatchingData.map((item, index) => {
            return (
              <div key={index}>
                <h2>Color Matching for RSN: {RSN}</h2>
                <table border="1" style={{ width: "100%", marginTop: "20px" }}>
                  <thead>
                    <tr>
                      <th>ColorId</th>
                      <th>Matching Name</th>
                      <th>Panel</th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <td>{item.ColorId}</td>
                      <td>{item.MatchingName}</td>
                      <td>{item.Panel}</td>
                    </tr>
                  </tbody>
                </table>

                {/* Now, display the color detail for each ColorId */}
                <ShowColorDetail ColorId={item.ColorId} />
              </div>
            );
          })}
        </div>
      ) : (
        <p>No color matching details available for this RSN.</p>
      )}
    </div>
  );
};

export default ShowColor;
