import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { updateKnittingDetails, getMachineNos } from "../API/SampleApi";

const UpdateKnittingDetails = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { RSN, knittingData } = location.state || {};

  const [formData, setFormData] = useState(knittingData);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [machineNos, setMachineNos] = useState([]);

  useEffect(() => {
    const fetchMachineNos = async () => {
      try {
        const response = await getMachineNos();
        setMachineNos(response.data); // Set the machine numbers to state
      } catch (error) {
        setError("Failed to fetch machine numbers");
      }
    };

    fetchMachineNos();
    if (!knittingData) {
      setError("No knitting data available.");
    }
  }, [knittingData]);

  // Handle input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleChange = (e, field, type) => {
    const { value } = e.target;
    setFormData((prevData) => {
      const newFormData = { ...prevData };
      newFormData[field][type] = value;
      newFormData.Total = calculateTotal(newFormData);
      return newFormData;
    });
  };

  const calculateTotal = (data) => {
    let totalWeight = 0;
    let totalTime = 0;

    Object.keys(data).forEach((key) => {
      if (key !== "RSN" && key !== "Size" && key !== "Total") {
        const { Weight, Time } = data[key];
        totalWeight += parseFloat(Weight) || 0;
        totalTime += parseFloat(Time) || 0;
      }
    });

    return { Weight: totalWeight, Time: totalTime };
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    for (let field in formData) {
      if (typeof formData[field] === "object") {
        const { Weight, Time } = formData[field];
        if (Weight === "" || Time === "") {
          setError(`Both weight and time must be provided for ${field}`);
          return;
        }
      }
    }

    setLoading(true);
    setError(null);
    setSuccess(null);

    try {
      const response = await updateKnittingDetails(RSN, formData);
      setSuccess("Knitting details updated successfully!");
    } catch (error) {
      setError(
        error.message || "An error occurred while updating knitting details"
      );
    } finally {
      setLoading(false);
    }

    navigate(`/show-sample/${RSN}`, { state: { RSN } });
  };

  return (
    <div>
      <h1>Update Knitting Details</h1>
      <form onSubmit={handleSubmit}>
        <table border="1">
          <thead>
            <tr>
              <th>Field</th>
              <th>Weight</th>
              <th>Time</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>RSN:</td>
              <td colSpan="2">
                <input type="text" name="RSN" value={formData.RSN} disabled />
              </td>
            </tr>
            <tr>
              <td>Size:</td>
              <td colSpan="2">
                <input
                  type="text"
                  name="Size"
                  value={formData.Size}
                  onChange={(e) =>
                    setFormData({ ...formData, Size: e.target.value })
                  }
                  required
                />
              </td>
            </tr>
            {[
              "FrontRight",
              "FrontLeft",
              "FrontComplete",
              "BackRight",
              "BackLeft",
              "BackComplete",
              "SleeveRight",
              "SleeveLeft",
              "BothSleeves",
              "Tape",
              "Collar",
              "Kharcha1",
              "Kharcha2",
              "Kharcha3",
            ].map((field) => (
              <tr key={field}>
                <td>{field}</td>
                <td>
                  <input
                    type="number"
                    value={formData[field].Weight}
                    onChange={(e) => handleChange(e, field, "Weight")}
                    placeholder="Weight"
                    required
                  />
                </td>
                <td>
                  <input
                    type="number"
                    value={formData[field].Time}
                    onChange={(e) => handleChange(e, field, "Time")}
                    placeholder="Time"
                    required
                  />
                </td>
                <td>
                  <select
                    value={formData[field].MachineNo || ""}
                    onChange={(e) => handleChange(e, field, "MachineNo")}
                    required
                  >
                    <option value="">Select Machine No</option>
                    {machineNos.map((machineNo) => (
                      <option key={machineNo} value={machineNo}>
                        {machineNo}
                      </option>
                    ))}
                  </select>
                </td>
              </tr>
            ))}
            <tr>
              <td>Total</td>
              <td>{formData.Total.Weight}</td>
              <td>{formData.Total.Time}</td>
            </tr>
          </tbody>
        </table>

        <button type="submit" disabled={loading}>
          {loading ? "Updating..." : "Update"}
        </button>
      </form>

      {error && <p style={{ color: "red" }}>{error}</p>}
      {success && <p style={{ color: "green" }}>{success}</p>}
    </div>
  );
};

export default UpdateKnittingDetails;
