import React, { useState } from 'react';
import axios from 'axios';

const AddColorDetails = () => {
  // Define initial state for the form
  const initialState = {
    ColorId: '',
    Size: '',
    BaseColor: '',
    colors: Array(14).fill({ name: '', weight: '' }), // Array for Color1 to Color14, each having 'name' and 'weight'
    totalWeight: 0, // Total weight field
  };

  const [formData, setFormData] = useState([initialState]);

  // Handle change for input fields (ColorId, Size, BaseColor, and each Color Name/Weight)
  const handleInputChange = (e, index, field) => {
    const { name, value } = e.target;
    const updatedFormData = [...formData];

    if (field === 'colors') {
      updatedFormData[index].colors[name] = value;
    } else {
      updatedFormData[index][name] = value;
    }

    // Recalculate total weight whenever a weight field is changed
    if (field === 'colors' && name.includes('weight')) {
      updatedFormData[index].totalWeight = updatedFormData[index].colors.reduce((sum, color) => {
        return sum + (parseFloat(color.weight) || 0); // Sum up all color weights, defaulting to 0 if invalid
      }, 0);
    }

    setFormData(updatedFormData);
  };

  // Handle adding a new row
  const handleAddRow = () => {
    setFormData([...formData, initialState]);
  };

  // API call to add color details
  const addColorDetail = async (colorData) => {
    try {
      const response = await axios.post(`${API_URL}/add-color-detail`, colorData);
      return response.data;
    } catch (error) {
      console.error("Error adding color detail:", error);
      throw error;
    }
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Prepare the data in the expected format
    const colorData = formData.map(row => {
      const colors = row.colors.reduce((acc, color, index) => {
        acc[`Color${index + 1}`] = { name: color.name, weight: color.weight };
        return acc;
      }, {});

      return {
        ColorId: row.ColorId,
        Size: row.Size,
        BaseColor: row.BaseColor,
        totalWeight: row.totalWeight, // Add totalWeight to the object
        ...colors, // Add Color1, Color2, ... Color14 to the object
      };
    });

    try {
      // Send the data to the backend via the API call
      const response = await Promise.all(colorData.map(data => addColorDetail(data)));
      console.log(response); // Log the successful response
    } catch (error) {
      console.error("Error submitting form:", error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <table border="1">
        <thead>
          <tr>
            <th>ColorId</th>
            <th>Size</th>
            <th>Base Color</th>
            <th>Color 1 Name</th>
            <th>Color 1 Weight</th>
            <th>Color 2 Name</th>
            <th>Color 2 Weight</th>
            <th>Color 3 Name</th>
            <th>Color 3 Weight</th>
            <th>Color 4 Name</th>
            <th>Color 4 Weight</th>
            <th>Color 5 Name</th>
            <th>Color 5 Weight</th>
            <th>Color 6 Name</th>
            <th>Color 6 Weight</th>
            <th>Color 7 Name</th>
            <th>Color 7 Weight</th>
            <th>Color 8 Name</th>
            <th>Color 8 Weight</th>
            <th>Color 9 Name</th>
            <th>Color 9 Weight</th>
            <th>Color 10 Name</th>
            <th>Color 10 Weight</th>
            <th>Color 11 Name</th>
            <th>Color 11 Weight</th>
            <th>Color 12 Name</th>
            <th>Color 12 Weight</th>
            <th>Color 13 Name</th>
            <th>Color 13 Weight</th>
            <th>Color 14 Name</th>
            <th>Color 14 Weight</th>
            <th>Total Weight</th>
          </tr>
        </thead>
        <tbody>
          {formData.map((row, rowIndex) => (
            <tr key={rowIndex}>
              <td>
                <input
                  type="text"
                  name="ColorId"
                  value={row.ColorId}
                  onChange={(e) => handleInputChange(e, rowIndex, 'ColorId')}
                />
              </td>
              <td>
                <input
                  type="text"
                  name="Size"
                  value={row.Size}
                  onChange={(e) => handleInputChange(e, rowIndex, 'Size')}
                />
              </td>
              <td>
                <input
                  type="text"
                  name="BaseColor"
                  value={row.BaseColor}
                  onChange={(e) => handleInputChange(e, rowIndex, 'BaseColor')}
                />
              </td>

              {/* Loop through Color1 to Color14 */}
              {row.colors.map((color, colorIndex) => (
                <React.Fragment key={colorIndex}>
                  <td>
                    <input
                      type="text"
                      name={colorIndex}
                      value={color.name}
                      placeholder={`Color ${colorIndex + 1} Name`}
                      onChange={(e) => handleInputChange(e, rowIndex, 'colors')}
                    />
                  </td>
                  <td>
                    <input
                      type="number"
                      name={colorIndex}
                      value={color.weight}
                      placeholder={`Color ${colorIndex + 1} Weight`}
                      onChange={(e) => handleInputChange(e, rowIndex, 'colors')}
                    />
                  </td>
                </React.Fragment>
              ))}

              {/* Total Weight */}
              <td>
                <input
                  type="number"
                  name="totalWeight"
                  value={row.totalWeight}
                  readOnly
                  style={{ backgroundColor: '#f0f0f0' }} // Make it read-only
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <button type="button" onClick={handleAddRow}>Add Row</button>
      <button type="submit">Submit</button>
    </form>
  );
};

export default AddColorDetails;
